{
  "name": "HEORepo - Health Economics & Outcomes Research Repository",
  "description": "A sophisticated digital library for Health Economics and Outcomes Research (HEOR) resources, featuring curated collections, intelligent search, and AI-powered insights."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface Resource {
  id: string;
  title: string;
  description: string;
  url: string;
  domain: string;
  imageUrl: string;
  addedDate: string;
  contributor: string;
  category: string;
  isBookmarked?: boolean;
}

export interface Collection {
  id: string;
  name: string;
  icon: string;
  description: string;
}

export enum Theme {
  ALL = 'all',
  ECONOMIC_MODELING = 'economic-modeling',
  REAL_WORLD_EVIDENCE = 'rwe',
  HEALTH_TECHNOLOGY_ASSESSMENT = 'hta',
  PATIENT_REPORTED_OUTCOMES = 'pro',
  SYSTEMATIC_REVIEWS = 'systematic-reviews',
  POLICY_PRICING = 'policy-pricing'
}

import { GoogleGenAI, Type } from "@google/genai";

const getAI = () => new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const askHEORAssistant = async (query: string, context?: string) => {
  const ai = getAI();
  const systemInstruction = `
    You are an expert HEOR (Health Economics and Outcomes Research) Assistant for the website HEORepo.
    Your goal is to help researchers find relevant resources, explain complex terms (like ICER, QALY, RWE, or Markov Models), 
    and provide summaries of the repository's collections.
    Keep responses professional, academic, yet accessible.
    If the user asks for a summary of a theme, use the provided context about the theme.
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: context ? `Context: ${context}\n\nUser Query: ${query}` : query,
      config: {
        systemInstruction,
        temperature: 0.7,
      },
    });

    return response.text;
  } catch (error) {
    console.error("Gemini Error:", error);
    return "I'm sorry, I encountered an error while processing your HEOR query. Please try again.";
  }
};

export const getSmartSummary = async (themeName: string) => {
  const ai = getAI();
  const response = await ai.models.generateContent({
    model: "gemini-3-flash-preview",
    contents: `Generate a 2-sentence professional overview of why "${themeName}" is critical in Health Economics and Outcomes Research today.`,
  });
  return response.text;
};

