import React, { useState, useEffect, useMemo } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import { COLLECTIONS, RESOURCES } from './data';
import { askHEORAssistant, getSmartSummary } from './services/gemini';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [aiResponse, setAiResponse] = useState<string | null>(null);
  const [isAiLoading, setIsAiLoading] = useState(false);
  const [smartSummary, setSmartSummary] = useState<string | null>(null);

  const filteredResources = useMemo(() => {
    return RESOURCES.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSearch;
    });
  }, [activeTheme, searchQuery]);

  const currentCollection = useMemo(() => 
    COLLECTIONS.find(c => c.id === activeTheme), 
    [activeTheme]
  );

  useEffect(() => {
    if (activeTheme !== 'all' && currentCollection) {
      setSmartSummary(null);
      getSmartSummary(currentCollection.name).then(setSmartSummary);
    } else {
      setSmartSummary(null);
    }
  }, [activeTheme, currentCollection]);

  const handleAskAI = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const input = (e.currentTarget.elements.namedItem('aiInput') as HTMLInputElement).value;
    if (!input) return;

    setIsAiLoading(true);
    setAiResponse(null);
    const context = currentCollection ? `Theme: ${currentCollection.name}. Description: ${currentCollection.description}` : "All Collections";
    const res = await askHEORAssistant(input, context);
    setAiResponse(res || "I couldn't generate a response.");
    setIsAiLoading(false);
  };

  return (
    <div className="flex min-h-screen bg-slate-50 text-slate-900 overflow-hidden">
      <Sidebar 
        collections={COLLECTIONS} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
      />

      <main className="flex-1 overflow-y-auto flex flex-col relative h-screen">
        {/* Header */}
        <header className="sticky top-0 z-30 bg-white/80 backdrop-blur-md border-b border-slate-200 px-4 py-4 lg:px-10 flex items-center justify-between">
          <div className="flex items-center gap-4 lg:gap-0">
            <button 
              onClick={() => setIsSidebarOpen(true)}
              className="lg:hidden p-2 hover:bg-slate-100 rounded-lg"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" />
              </svg>
            </button>
            <div className="hidden lg:block">
              <h2 className="text-xl font-bold tracking-tight">Repository</h2>
            </div>
          </div>

          <div className="flex-1 max-w-2xl mx-4 lg:mx-8">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-slate-400">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <input
                type="text"
                placeholder="Search HEOR articles, datasets, or models..."
                className="block w-full pl-10 pr-3 py-2.5 bg-slate-100 border-none rounded-2xl text-sm focus:ring-2 focus:ring-blue-500 focus:bg-white transition-all"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
          </div>

          <div className="flex items-center gap-2 lg:gap-4">
            <div className="hidden md:flex flex-col items-end mr-2">
              <span className="text-[10px] font-bold text-slate-400 uppercase">Researcher</span>
              <span className="text-xs font-bold">Admin Panel</span>
            </div>
            <div className="w-10 h-10 rounded-full bg-slate-200 border-2 border-white shadow-sm overflow-hidden">
              <img src="https://picsum.photos/seed/user/100/100" alt="User avatar" />
            </div>
          </div>
        </header>

        {/* Hero Section */}
        <div className="px-4 lg:px-10 py-10 lg:py-16 bg-gradient-to-b from-white to-transparent">
          <div className="max-w-7xl mx-auto">
            <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-8">
              <div className="max-w-2xl">
                <div className="inline-flex items-center gap-2 px-3 py-1 bg-blue-50 text-blue-600 rounded-full text-[11px] font-bold uppercase tracking-wider mb-4 border border-blue-100">
                  <span className="flex h-2 w-2 rounded-full bg-blue-600 animate-pulse"></span>
                  HEOR Digital Library
                </div>
                <h1 className="text-4xl lg:text-6xl font-black text-slate-900 mb-6 leading-[1.1]">
                  {activeTheme === 'all' ? 'Advanced HEOR Repository' : currentCollection?.name}
                </h1>
                <p className="text-lg lg:text-xl text-slate-500 font-medium leading-relaxed max-w-xl">
                  {activeTheme === 'all' 
                    ? 'The ultimate source for Health Economics & Outcomes Research documentation, methodologies, and benchmarks.' 
                    : smartSummary || currentCollection?.description}
                </p>
              </div>

              <div className="lg:w-96">
                <div className="bg-white rounded-3xl shadow-xl shadow-slate-200/50 p-6 border border-slate-100">
                  <div className="flex items-center gap-3 mb-4">
                    <div className="w-8 h-8 rounded-lg bg-blue-600 flex items-center justify-center text-white">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5">
                        <path d="M12 2a10 10 0 1 0 10 10H12V2z" />
                        <path d="M12 12L2.1 12" />
                        <path d="M12 12l9.9 0" />
                        <path d="M12 12l0 10" />
                        <path d="M16 16l4 4" />
                      </svg>
                    </div>
                    <h3 className="font-bold text-slate-900">HEOR Assistant</h3>
                  </div>

                  <form onSubmit={handleAskAI} className="space-y-3">
                    <input 
                      name="aiInput"
                      type="text" 
                      placeholder="Explain ICER vs QALY..." 
                      className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
                    />
                    <button 
                      type="submit"
                      disabled={isAiLoading}
                      className="w-full bg-blue-600 text-white py-2.5 rounded-xl text-sm font-bold shadow-lg shadow-blue-200 hover:bg-blue-700 transition-all disabled:opacity-50"
                    >
                      {isAiLoading ? 'Thinking...' : 'Consult AI'}
                    </button>
                  </form>

                  {aiResponse && (
                    <div className="mt-4 p-4 bg-blue-50/50 rounded-2xl border border-blue-100 animate-in fade-in slide-in-from-top-2 duration-300">
                      <p className="text-xs leading-relaxed text-slate-700 whitespace-pre-wrap">{aiResponse}</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Resource Grid */}
        <div className="px-4 lg:px-10 pb-20">
          <div className="max-w-7xl mx-auto">
            <div className="flex items-center justify-between mb-8">
              <h3 className="text-sm font-bold text-slate-400 uppercase tracking-widest">
                Resources ({filteredResources.length})
              </h3>
              <div className="flex items-center gap-2">
                <button className="px-4 py-2 bg-white border border-slate-200 rounded-xl text-xs font-bold hover:bg-slate-50 transition-colors">
                  Sort By: Recent
                </button>
                <button className="p-2 bg-white border border-slate-200 rounded-xl hover:bg-slate-50 transition-colors">
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-4 h-4 text-slate-500" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <rect x="3" y="3" width="7" height="7" /><rect x="14" y="3" width="7" height="7" /><rect x="14" y="14" width="7" height="7" /><rect x="3" y="14" width="7" height="7" />
                  </svg>
                </button>
              </div>
            </div>

            {filteredResources.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8 lg:gap-12">
                {filteredResources.map((res) => (
                  <ResourceCard key={res.id} resource={res} />
                ))}
              </div>
            ) : (
              <div className="py-20 flex flex-col items-center justify-center text-center">
                <div className="w-20 h-20 bg-slate-100 rounded-full flex items-center justify-center text-slate-300 mb-4">
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-10 h-10" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.172 9.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
                <h4 className="text-xl font-bold text-slate-900 mb-2">No matching resources found</h4>
                <p className="text-slate-500">Try adjusting your filters or search terms.</p>
                <button 
                  onClick={() => {setActiveTheme('all'); setSearchQuery('');}}
                  className="mt-6 text-blue-600 font-bold hover:underline"
                >
                  Clear all filters
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <footer className="mt-auto py-10 border-t border-slate-200 bg-white">
          <div className="max-w-7xl mx-auto px-4 lg:px-10 flex flex-col md:flex-row justify-between items-center gap-6">
            <div className="flex items-center gap-3">
              <div className="w-8 h-8 bg-slate-900 rounded-lg flex items-center justify-center text-white">
                <span className="font-bold text-sm">H</span>
              </div>
              <p className="text-sm font-bold text-slate-900">HEORepo  2026</p>
            </div>
            <div className="flex items-center gap-8">
              <a href="#" className="text-sm text-slate-500 hover:text-slate-900 transition-colors">Terms</a>
              <a href="#" className="text-sm text-slate-500 hover:text-slate-900 transition-colors">Privacy</a>
              <a href="#" className="text-sm text-slate-500 hover:text-slate-900 transition-colors">Methodology</a>
              <a href="#" className="text-sm text-slate-500 hover:text-slate-900 transition-colors">API Docs</a>
            </div>
            <div className="flex items-center gap-4">
              <div className="w-8 h-8 rounded-full bg-slate-100 flex items-center justify-center text-slate-400 cursor-pointer hover:bg-slate-200 transition-all">
