import React, { useState, useMemo } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import { COLLECTIONS, RESOURCES } from './data';

const BRAND_BLUE = '#2563EB';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const filteredResources = useMemo(() => {
    return RESOURCES.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSearch;
    });
  }, [activeTheme, searchQuery]);

  const currentCollection = useMemo(() => 
    COLLECTIONS.find(c => c.id === activeTheme), 
    [activeTheme]
  );

  return (
    <div className="flex h-screen bg-[#F9FAFB] text-slate-900 overflow-hidden font-['Inter']">
      <Sidebar 
        collections={COLLECTIONS} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Content Header Area */}
        <div className="bg-white border-b border-[#E5E7EB] px-8 py-10">
          <div className="max-w-[1400px] mx-auto flex flex-col md:flex-row md:items-end justify-between gap-8">
            <div className="flex flex-col">
              <div className="mb-4">
                <span className="px-3 py-1 bg-[#EFF6FF] text-[#2563EB] rounded-full text-[10px] font-black uppercase tracking-widest">
                  {activeTheme === 'all' ? 'GENERAL' : currentCollection?.name.toUpperCase()}
                </span>
              </div>
              <h1 className="text-4xl leading-tight tracking-tight">
                <span className="font-black text-[#111827]">{activeTheme === 'all' ? 'General' : currentCollection?.name}</span>{' '}
                <span className="font-light text-[#9CA3AF]">Vault</span>
              </h1>
            </div>

            <div className="flex flex-col md:items-end gap-6 w-full md:w-auto">
              <div className="flex items-center gap-4 w-full md:w-[500px]">
                <div className="relative flex-1">
                  <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-[#9CA3AF]">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                  </div>
                  <input
                    type="text"
                    placeholder="Search through repository index..."
                    className="block w-full pl-11 pr-4 py-3 bg-white border border-[#E5E7EB] rounded-full text-xs font-bold text-[#111827] focus:ring-1 focus:ring-[#2563EB] outline-none shadow-sm placeholder-[#9CA3AF]"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
                <button className="whitespace-nowrap px-6 py-3 bg-[#2563EB] text-white text-xs font-black uppercase tracking-widest rounded-full hover:bg-blue-700 transition-colors shadow-lg shadow-blue-500/20">
                  Help Enrich Repo
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Scrollable Content Grid Area */}
        <div className="flex-1 overflow-y-auto px-8 py-12">
          <div className="max-w-[1400px] mx-auto">
            {filteredResources.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                {filteredResources.map((res) => (
                  <ResourceCard key={res.id} resource={res} />
