import React, { useState, useMemo } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import { COLLECTIONS, RESOURCES } from './data';

const BRAND_BLUE = '#2563EB';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isChinese, setIsChinese] = useState(false);

  const filteredResources = useMemo(() => {
    return RESOURCES.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSearch;
    });
  }, [activeTheme, searchQuery]);

  const currentCollection = useMemo(() => 
    COLLECTIONS.find(c => c.id === activeTheme), 
    [activeTheme]
  );

  const collectionName = activeTheme === 'all' ? 'General' : currentCollection?.name || 'General';

  return (
    <div className="flex h-screen bg-[#F9FAFB] text-slate-900 overflow-hidden font-['Inter']">
      <Sidebar 
        collections={COLLECTIONS} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Compact Header Area with Reduced Font Sizes */}
        <header className="bg-white border-b border-[#E5E7EB] px-12 py-4 select-none">
          <div className="max-w-[1600px] mx-auto flex items-center justify-between gap-8">
            {/* Left: Title & Metadata */}
            <div className="flex flex-col gap-1">
              <div className="flex items-center gap-4">
                <div className="px-2.5 py-0.5 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[8px] font-[800] tracking-[0.1em] text-[#2563EB] uppercase antialiased">
                  {activeTheme === 'all' ? 'GENERAL' : currentCollection?.name.toUpperCase()}
                </div>
                <div className="text-[8px] font-[800] tracking-[0.1em] text-[#334155] uppercase antialiased">
                  {filteredResources.length} ITEMS
                </div>
              </div>
              <h1 className="text-[24px] font-[900] tracking-[-0.03em] leading-[1.1] text-[#0F172A] antialiased">
                {collectionName} <span className="text-[#D1D5DB]">Vault</span>
              </h1>
            </div>

            {/* Right: Search, Action & Language */}
            <div className="flex items-center gap-5">
              {/* Stadium Search Bar */}
              <div className="relative w-[280px]">
                <div className="absolute inset-y-0 left-0 pl-3.5 flex items-center pointer-events-none text-[#94A3B8]">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-3.5 w-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  placeholder={`Search in ${collectionName.toLowerCase()}...`}
                  className="block w-full pl-10 pr-5 py-2.5 bg-white border border-[#E2E8F0] rounded-full text-[12px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#2563EB] outline-none shadow-sm transition-all placeholder-[#94A3B8]"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>

              {/* Action Button */}
              <button className="flex items-center gap-2.5 px-6 py-3 bg-[#2563EB] text-white text-[13px] font-[800] rounded-[18px] hover:bg-blue-700 transition-all shadow-[0_8px_20px_rgba(37,99,235,0.15)] active:scale-[0.98]">
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-6h2v6zm0-8h-2V7h2v2z" />
                </svg>
                Help Enrich Repo
              </button>

              {/* Language Toggle */}
              <div className="flex items-center gap-2.5 ml-2">
                <span className={`text-[11px] font-[800] ${!isChinese ? 'text-[#2563EB]' : 'text-[#94A3B8]'}`}>EN</span>
                <button 
                  onClick={() => setIsChinese(!isChinese)}
                  className="w-10 h-5 bg-[#E2E8F0] rounded-full relative transition-colors duration-300 focus:outline-none"
                >
                  <div className={`absolute top-0.5 w-4 h-4 bg-white rounded-full transition-all duration-300 shadow-sm ${isChinese ? 'left-5.5' : 'left-0.5'}`} />
                </button>
                <span className={`text-[11px] font-[800] ${isChinese ? 'text-[#2563EB]' : 'text-[#94A3B8]'}`}></span>
              </div>
            </div>
          </div>
        </header>

        {/* Scrollable Content Grid Area */}
        <div className="flex-1 overflow-y-auto px-12 py-10">
          <div className="max-w-[1600px] mx-auto">
            {filteredResources.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-10">
                {filteredResources.map((res) => (
                  <ResourceCard key={res.id} resource={res} />
                ))}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center py-32 text-center">
                <div className="w-20 h-20 bg-white border border-[#E5E7EB] rounded-3xl flex items-center justify-center text-[#9CA3AF] mb-8 shadow-sm">
                  <svg className="w-10 h-10" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}><path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" /></svg>
                </div>
                <h4 className="text-2xl font-black text-[#111827] mb-3">No matching archive data</h4>
                <p className="text-base text-[#6B7280]">Try clearing your search or exploring a specific thematic collection.</p>
              </div>
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t-[3px] border-t-[#2563EB] py-4 px-12 flex items-center justify-between">
          <div className="flex items-center gap-10">
            <span className="text-[11px] font-black text-[#6B7280] tracking-[0.25em] uppercase">COLLECTIONS: {COLLECTIONS.length + 74}</span>
            <span className="text-[11px] font-black text-[#6B7280] tracking-[0.25em] uppercase hidden sm:inline">NODES: 1,429 ACTIVE</span>
          </div>
          <div className="flex items-center gap-3">
             <span className="text-[11px] font-black text-[#6B7280] tracking-[0.15em] uppercase">HEOR CLOUD SYNC ACTIVE</span>
             <div className="w-2.5 h-2.5 rounded-full bg-[#2563EB] animate-pulse"></div>
          </div>
        </footer>
      </main>
    </div>
  );
};

