import React, { useState, useMemo } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import { COLLECTIONS, RESOURCES } from './data';

const BRAND_BLUE = '#2563EB';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isChinese, setIsChinese] = useState(false);

  const filteredResources = useMemo(() => {
    return RESOURCES.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSearch;
    });
  }, [activeTheme, searchQuery]);

  const currentCollection = useMemo(() => 
    COLLECTIONS.find(c => c.id === activeTheme), 
    [activeTheme]
  );

  const collectionName = activeTheme === 'all' ? 'General' : currentCollection?.name || 'General';

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter']">
      <Sidebar 
        collections={COLLECTIONS} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Seamless Header - Lightened background from #F9FAFB to #FCFDFF */}
        <header className="bg-[#FCFDFF] px-12 pt-10 pb-5 select-none">
          <div className="max-w-[1600px] mx-auto flex items-end justify-between gap-8">
            {/* Left: Title & Metadata */}
            <div className="flex flex-col gap-2">
              <div className="flex items-center gap-4">
                <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[10px] font-[800] tracking-[0.15em] text-[#2563EB] uppercase antialiased">
                  {activeTheme === 'all' ? 'GENERAL' : currentCollection?.name.toUpperCase()}
                </div>
                <div className="text-[10px] font-[800] tracking-[0.15em] text-[#64748B] uppercase antialiased">
                  {filteredResources.length} ITEMS
                </div>
              </div>
              {/* Reduced font size by 15% (54px to 46px) */}
              <h1 className="text-[46px] font-[900] tracking-[-0.04em] leading-[0.9] text-[#0F172A] antialiased">
                {collectionName} <span className="text-[#D1D5DB]">Vault</span>
              </h1>
            </div>

            {/* Right: Search, Action & Language */}
            <div className="flex items-center gap-6 pb-1">
              {/* 'Cutout' Style Search Bar */}
              <div className="relative w-[340px]">
                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-[#94A3B8]">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  placeholder={`Search in ${collectionName.toLowerCase()}...`}
                  className="block w-full pl-11 pr-5 py-3.5 bg-white border border-[#F1F5F9] rounded-full text-[14px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#2563EB] outline-none shadow-sm transition-all placeholder-[#94A3B8]"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>

              {/* Action Button */}
              <button className="flex items-center gap-3 px-8 py-4 bg-[#2563EB] text-white text-[14px] font-[800] rounded-[22px] hover:bg-blue-700 transition-all shadow-[0_12px_24px_rgba(37,99,235,0.18)] active:scale-[0.98]">
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
