import React, { useState, useEffect } from 'react';
import { Collection, Theme } from '../types';

interface SidebarProps {
  collections: Collection[];
  activeTheme: string;
  onThemeSelect: (id: string) => void;
  isOpen: boolean;
  onClose: () => void;
}

const BRAND_BLUE = '#2563EB';
const DARK_NAVY = '#121A26';
const SLATE_LIGHTER = '#94A3B8'; // Lightened from original grey
const SLATE_EXTRA_LIGHTER = '#CBD5E1'; // Very light grey

const TypingTagline: React.FC = () => {
  const words = ['HEOR', 'MARKOV', 'PSA'];
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentText, setCurrentText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [typingSpeed, setTypingSpeed] = useState(75); 

  useEffect(() => {
    const handleTyping = () => {
      const fullWord = words[currentWordIndex];

      if (!isDeleting) {
        // Typing
        setCurrentText(fullWord.substring(0, currentText.length + 1));
        setTypingSpeed(75);

        if (currentText === fullWord) {
          // Pause at the end of word
          setTimeout(() => setIsDeleting(true), 1200);
        }
      } else {
        // Deleting
        setCurrentText(fullWord.substring(0, currentText.length - 1));
        setTypingSpeed(50);

        if (currentText === '') {
          setIsDeleting(false);
          setCurrentWordIndex((prev) => (prev + 1) % words.length);
        }
      }
    };

    const timer = setTimeout(handleTyping, typingSpeed);
    return () => clearTimeout(timer);
  }, [currentText, isDeleting, currentWordIndex, typingSpeed]);

  return (
    <span style={{ color: BRAND_BLUE, fontWeight: 900, minHeight: '1em', display: 'inline-block' }}>
      {currentText}
    </span>
  );
};

const Sidebar: React.FC<SidebarProps> = ({ collections, activeTheme, onThemeSelect, isOpen, onClose }) => {
  return (
    <>
      {isOpen && (
        <div className="fixed inset-0 bg-black/10 backdrop-blur-sm z-40 lg:hidden" onClick={onClose} />
      )}

      <aside className={`
        fixed inset-y-0 left-0 z-50 w-[280px] lg:w-1/6 lg:min-w-[240px] bg-white border-r border-[#E5E7EB] transform transition-transform duration-300 ease-in-out lg:relative lg:translate-x-0
        ${isOpen ? 'translate-x-0' : '-translate-x-full'} flex flex-col
      `}>
        {/* Logo Section */}
        <div className="p-8 pb-4">
          <div className="flex flex-col items-start select-none">
            {/* Wordmark with combined R and vertical color split */}
            <div className="flex items-baseline tracking-[-0.05em] font-[900] text-[34px] leading-none antialiased">
              <span style={{ color: DARK_NAVY }}>HEO</span>
              <span style={{ 
                backgroundImage: `linear-gradient(to right, ${DARK_NAVY} 50%, ${BRAND_BLUE} 50%)`,
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                display: 'inline-block',
                position: 'relative'
              }}>R</span>
              <span style={{ color: BRAND_BLUE }}>epo</span>
              <span 
                className="ml-[4px] inline-block" 
                style={{ 
                  backgroundColor: BRAND_BLUE, 
                  width: '8px', 
                  height: '8px',
                  marginBottom: '4px' 
                }}
              ></span>
            </div>

            {/* Tagline: High-contrast labels and vibrant typing effect */}
            <div 
              className="flex flex-col gap-[4px] uppercase mt-3.5 antialiased tracking-[0.3em] leading-[1.0]"
              style={{ fontSize: '10px' }}
            >
              <span style={{ color: SLATE_LIGHTER, fontWeight: 900 }}>REPOSITORY</span>
              <span style={{ color: SLATE_EXTRA_LIGHTER, fontWeight: 900 }}>FOR</span>
