export interface Resource {
  id: string;
  title: string;
  description: string;
  url: string;
  domain: string;
  imageUrl: string;
  addedDate: string;
  contributor: string;
  category: string;
  subCategory?: string;
  fileType?: string;
  status?: 'verified' | 'warning' | 'new';
  isBookmarked?: boolean;
}

export interface Collection {
  id: string;
  name: string;
  icon: string;
  description: string;
  subCategories?: string[];
}

export enum Theme {
  ALL = 'all',
  ECONOMIC_MODELING = 'modelling',
  SYSTEMATIC_REVIEWS = 'meta-analysis',
  NEWS = 'news'
}

import React, { useState, useMemo, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import { COLLECTIONS, RESOURCES } from './data';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Reset sub-category when theme changes
  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const filteredResources = useMemo(() => {
    return RESOURCES.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery]);

  const currentCollection = useMemo(() => 
    COLLECTIONS.find(c => c.id === activeTheme), 
    [activeTheme]
  );

  const collectionName = activeTheme === 'all' ? 'General' : currentCollection?.name || 'General';
  const subCategories = currentCollection?.subCategories || [];

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter']">
      <Sidebar 
        collections={COLLECTIONS} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white px-8 pt-10 pb-6 select-none border-b border-[#F1F5F9]/60">
          <div className="max-w-[1440px] mx-auto">
            <div className="flex items-end justify-between gap-8 mb-8">
              <div className="flex flex-col gap-3">
                <div className="flex items-center gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[10px] font-[800] tracking-[0.15em] text-[#2563EB] uppercase antialiased">
                    {activeTheme === 'all' ? 'GENERAL' : currentCollection?.name.toUpperCase()}
                  </div>
                  <div className="text-[10px] font-[800] tracking-[0.15em] text-[#64748B] uppercase antialiased">
                    {filteredResources.length} ITEMS
                  </div>
                </div>
                <h1 className="text-[48px] font-[900] tracking-[-0.04em] leading-[0.9] text-[#0F172A] antialiased">
                  {collectionName} <span className="text-[#D1D5DB]">Vault</span>
                </h1>
              </div>

              <div className="flex items-center gap-6 pb-1">
                <div className="relative w-[380px]">
                  <div className="absolute inset-y-0 left-0 pl-5 flex items-center pointer-events-none text-[#94A3B8]">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                  </div>
                  <input
                    type="text"
                    placeholder="Search repository..."
                    className="block w-full pl-12 pr-6 py-3.5 bg-[#F8FAFC] border border-[#F1F5F9] rounded-full text-[15px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#4F46E5] focus:bg-white outline-none shadow-sm transition-all placeholder-[#94A3B8]"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>

                <button className="flex items-center gap-2.5 px-8 py-3.5 bg-[#2563EB] text-white text-[15px] font-[700] rounded-full hover:bg-[#1d4ed8] transition-all shadow-md active:scale-[0.98]">
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="3">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M12 4v16m8-8H4" />
                  </svg>
                  Contribute
                </button>
              </div>
            </div>

            {/* Sub-category Filter Bar */}
            {activeTheme !== 'all' && subCategories.length > 0 && (
              <div className="flex items-center gap-3 overflow-x-auto pb-2 scrollbar-hide">
                <button
                  onClick={() => setActiveSubCategory('all')}
                  className={`px-6 py-2.5 rounded-full text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-200 whitespace-nowrap ${
                    activeSubCategory === 'all'
                    ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                    : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                  }`}
                >
                  All {collectionName}s
                </button>
                {subCategories.map((sub) => (
                  <button
                    key={sub}
                    onClick={() => setActiveSubCategory(sub)}
                    className={`px-6 py-2.5 rounded-full text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-200 whitespace-nowrap ${
                      activeSubCategory === sub
                      ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                      : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                    }`}
                  >
                    {sub}
                  </button>
                ))}
              </div>
            )}
          </div>
        </header>

        {/* Scrollable Content Grid Area */}
        <div className="flex-1 overflow-y-auto px-8 py-10">
          <div className="max-w-[1440px] mx-auto">
            {filteredResources.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-[24px] justify-items-center">
                {filteredResources.map((res) => (
                  <ResourceCard key={res.id} resource={res} />
                ))}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center py-32 text-center">
                <div className="w-16 h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-6 shadow-sm">
                  <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                    <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                  </svg>
                </div>
                <h4 className="text-xl font-[800] text-[#111827] mb-2">Empty Archive</h4>
                <p className="text-[14px] text-[#6B7280]">Adjust your filters or try a different search term.</p>
              </div>
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-8 flex items-center justify-between">
          <div className="flex items-center gap-8">
            <span className="text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase">SYSTEM VERSION 2.4.0</span>
            <span className="text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase hidden sm:inline">DATABASE NODES: 1,429 ACTIVE</span>
          </div>
          <div className="flex items-center gap-2">
             <div className="w-1.5 h-1.5 rounded-full bg-emerald-500"></div>
             <span className="text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">SYNC SECURE</span>
          </div>
        </footer>
      </main>
    </div>
  );
};

