import React, { useState } from 'react';
import { Resource, Collection, Theme } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ resources, setResources, collections, setCollections }) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections'>('resources');

  // Resource Management
  const updateResource = (id: string, field: keyof Resource, value: string) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (confirm('Are you sure you want to delete this resource?')) {
      setResources(prev => prev.filter(res => res.id !== id));
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'New resource description goes here...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'NEW.SITE.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management
  const addCollection = () => {
    const newCol: Collection = {
      id: `custom-${Math.random().toString(36).substr(2, 5)}`,
      name: 'New Collection',
      icon: '',
      description: 'Enter collection description...',
      subCategories: ['General']
    };
    setCollections([...collections, newCol]);
  };

  const deleteCollection = (id: string) => {
    if (confirm('Delete this collection? This will not delete associated resources.')) {
      setCollections(prev => prev.filter(c => c.id !== id));
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-100 shadow-sm overflow-hidden animate-card-entry">
      <div className="flex border-b border-slate-100">
        <button 
          onClick={() => setActiveTab('resources')}
