          onClick={() => setActiveTab('collections')}
          className={`px-8 py-5 text-[13px] font-bold uppercase tracking-wider transition-all ${activeTab === 'collections' ? 'text-[#2563EB] border-b-2 border-[#2563EB]' : 'text-slate-400'}`}
        >
          Manage Collections
        </button>
      </div>

      <div className="p-8">
        {activeTab === 'resources' ? (
          <div>
            <div className="flex justify-between items-center mb-6">
