                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-100">
                  {resources.map(res => (
                    <tr key={res.id} className="hover:bg-slate-50/50 transition-colors group">
                      <td className="px-6 py-4">
                        <div className="flex flex-col gap-1">
                          {editingId === res.id ? (
                            <>
                              <input 
                                className="text-[14px] font-bold text-[#0F172A] border-b border-blue-200 outline-none w-full bg-transparent"
                                value={res.title}
                                onChange={(e) => updateResource(res.id, 'title', e.target.value)}
                                placeholder="Title"
                              />
                              <input 
                                className="text-[12px] text-blue-500 border-b border-blue-100 outline-none w-full bg-transparent mt-1"
                                value={res.url}
                                onChange={(e) => updateResource(res.id, 'url', e.target.value)}
                                placeholder="URL (https://...)"
                              />
                              <input 
                                className="text-[10px] text-slate-400 border-b border-blue-100 outline-none w-full bg-transparent"
                                value={res.domain}
                                onChange={(e) => updateResource(res.id, 'domain', e.target.value)}
                                placeholder="Display Domain"
                              />
                            </>
                          ) : (
                            <>
                              <span className="text-[14px] font-bold text-[#0F172A]">{res.title}</span>
                              <span className="text-[11px] text-slate-400 truncate max-w-[200px]">{res.url}</span>
                            </>
