                Add Collection
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {collections.map(col => (
                <div key={col.id} className="p-6 rounded-2xl border border-slate-100 flex items-center justify-between group hover:border-blue-100 transition-all">
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-slate-50 rounded-xl flex items-center justify-center text-2xl">
                      {col.icon}
                    </div>
                    <div>
                      <h4 className="font-bold text-[#0F172A]">{col.name}</h4>
                      <p className="text-[12px] text-slate-400 truncate max-w-[200px]">{col.description}</p>
                    </div>
                  </div>
                  <button 
                    onClick={() => deleteCollection(col.id)}
                    className="p-2 text-slate-300 hover:text-red-500 transition-colors"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                    </svg>
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState } from 'react';
import { Resource, Collection, Theme } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ resources, setResources, collections, setCollections }) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingCollectionId, setEditingCollectionId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections'>('resources');

  // Resource Management Logic
  const updateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (window.confirm('Are you sure you want to delete this resource? This action cannot be undone.')) {
      setResources(prev => prev.filter(res => res.id !== id));
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'Describe this resource for users...',
      contributor: 'System Admin',
      url: 'https://example.com',
      domain: 'EXAMPLE.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL,
      subCategory: 'General'
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management Logic
  const updateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => col.id === id ? { ...col, [field]: value } : col));
  };

  const addCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    const newCol: Collection = {
      id: newId,
      name: 'New Collection',
      icon: '',
      description: 'Brief description of what this category contains...',
      subCategories: ['General']
    };
    setCollections([...collections, newCol]);
    setEditingCollectionId(newId);
  };

  const deleteCollection = (id: string) => {
    if (window.confirm('Delete this collection? Resources assigned to this category will still exist but may need reassignment.')) {
      setCollections(prev => prev.filter(c => c.id !== id));
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2">
        <button 
          onClick={() => setActiveTab('resources')}
          className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 ${
            activeTab === 'resources' 
            ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' 
            : 'text-slate-500 hover:bg-white/50'
          }`}
        >
          <div className="flex items-center justify-center gap-2">
            <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" /></svg>
            Resource Manager
          </div>
        </button>
        <button 
          onClick={() => setActiveTab('collections')}
          className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 ${
            activeTab === 'collections' 
            ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' 
            : 'text-slate-500 hover:bg-white/50'
          }`}
        >
          <div className="flex items-center justify-center gap-2">
            <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h7" /></svg>
            Collection Architecture
          </div>
        </button>
      </div>

      <div className="p-10">
        {activeTab === 'resources' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Active Vault Items</h2>
                <p className="text-slate-500 text-[14px]">Update titles, metadata, and linked repositories.</p>
              </div>
              <button 
                onClick={addResource}
                className="bg-[#2563EB] text-white px-8 py-4 rounded-full text-[13px] font-[800] uppercase tracking-widest hover:bg-blue-600 transition-all shadow-lg shadow-blue-200 flex items-center gap-3 active:scale-95"
              >
                <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M12 4v16m8-8H4" /></svg>
                New Entry
              </button>
            </div>

            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm">
              <table className="w-full text-left border-collapse">
                <thead className="bg-[#F8FAFC] border-b border-slate-200">
                  <tr>
                    <th className="px-8 py-5 text-[11px] font-[900] text-slate-400 uppercase tracking-[0.15em]">Resource Core</th>
                    <th className="px-8 py-5 text-[11px] font-[900] text-slate-400 uppercase tracking-[0.15em]">Classification</th>
                    <th className="px-8 py-5 text-[11px] font-[900] text-slate-400 uppercase tracking-[0.15em]">Metadata</th>
                    <th className="px-8 py-5 text-[11px] font-[900] text-slate-400 uppercase tracking-[0.15em] text-right">Edit</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => (
                    <tr key={res.id} className={`group transition-all ${editingId === res.id ? 'bg-blue-50/30' : 'hover:bg-slate-50/80'}`}>
                      <td className="px-8 py-6 max-w-[400px]">
                        {editingId === res.id ? (
                          <div className="space-y-3">
                            <input 
                              className="w-full text-[16px] font-[800] text-[#0F172A] border-b-2 border-blue-400 outline-none bg-transparent py-1"
                              value={res.title}
                              onChange={(e) => updateResource(res.id, 'title', e.target.value)}
                              placeholder="Resource Title"
                            />
                            <textarea 
                              className="w-full text-[13px] text-slate-600 border border-slate-200 rounded-xl p-3 outline-none bg-white focus:ring-2 focus:ring-blue-100"
                              value={res.description}
                              rows={2}
                              onChange={(e) => updateResource(res.id, 'description', e.target.value)}
                              placeholder="Brief description..."
                            />
                            <div className="flex gap-2">
                              <input 
                                className="flex-1 text-[11px] font-mono text-blue-600 bg-white border border-slate-200 rounded-lg p-2 outline-none"
                                value={res.url}
                                onChange={(e) => updateResource(res.id, 'url', e.target.value)}
                                placeholder="Target URL"
                              />
                              <input 
                                className="w-32 text-[10px] font-bold text-slate-400 bg-white border border-slate-200 rounded-lg p-2 outline-none uppercase"
                                value={res.domain}
                                onChange={(e) => updateResource(res.id, 'domain', e.target.value)}
                                placeholder="Domain Label"
                              />
                            </div>
                          </div>
                        ) : (
                          <div className="flex gap-4">
                            <div className="w-12 h-12 rounded-xl overflow-hidden shrink-0 shadow-sm border border-slate-100">
                                <img src={res.imageUrl} className="w-full h-full object-cover" />
                            </div>
                            <div className="flex flex-col gap-1 min-w-0">
                                <h4 className="text-[15px] font-[800] text-[#0F172A] truncate">{res.title}</h4>
                                <p className="text-[12px] text-slate-500 line-clamp-2">{res.description}</p>
                                <span className="text-[10px] font-bold text-blue-500 tracking-wider uppercase mt-1">{res.domain}</span>
                            </div>
                          </div>
                        )}
                      </td>
                      <td className="px-8 py-6">
                        {editingId === res.id ? (
                          <div className="flex flex-col gap-2">
                            <select 
                              className="text-[12px] font-[800] text-slate-700 bg-white border border-slate-200 rounded-lg p-2 outline-none uppercase tracking-wider"
                              value={res.category}
                              onChange={(e) => updateResource(res.id, 'category', e.target.value)}
                            >
                              <option value="all">General</option>
                              {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                            </select>
                            <input 
                              className="text-[12px] font-[800] text-slate-500 bg-white border border-slate-200 rounded-lg p-2 outline-none uppercase tracking-wider"
                              value={res.subCategory}
                              onChange={(e) => updateResource(res.id, 'subCategory', e.target.value)}
                              placeholder="Sub-Category"
                            />
                          </div>
                        ) : (
                          <div className="flex flex-col gap-1.5">
                            <span className="inline-flex px-3 py-1 rounded-full bg-blue-50 text-blue-600 text-[10px] font-[900] uppercase tracking-wider border border-blue-100 self-start">
                              {res.category}
                            </span>
                            <span className="px-3 py-1 text-[10px] font-[800] text-slate-400 uppercase tracking-widest italic">
                              {res.subCategory || 'No Sub-Cat'}
                            </span>
                          </div>
                        )}
                      </td>
                      <td className="px-8 py-6">
                        {editingId === res.id ? (
                          <div className="space-y-2">
                            <input 
                              className="w-full text-[11px] font-[800] text-slate-700 bg-white border border-slate-200 rounded-lg p-2 outline-none"
                              value={res.contributor}
                              onChange={(e) => updateResource(res.id, 'contributor', e.target.value)}
                              placeholder="Author Name"
                            />
                            <div className="text-[10px] font-bold text-slate-300 uppercase px-1">Adding: {res.addedDate}</div>
                          </div>
                        ) : (
                          <div className="flex flex-col">
                            <span className="text-[11px] font-[900] text-slate-800 tracking-tight">{res.contributor}</span>
                            <span className="text-[10px] font-bold text-slate-400 uppercase mt-0.5">{res.addedDate}</span>
                          </div>
                        )}
                      </td>
                      <td className="px-8 py-6 text-right">
                        <div className="flex items-center justify-end gap-2">
                          <button 
                            onClick={() => setEditingId(editingId === res.id ? null : res.id)}
                            className={`p-3 rounded-2xl transition-all duration-300 ${
                                editingId === res.id 
                                ? 'bg-blue-600 text-white shadow-lg shadow-blue-200 scale-110' 
                                : 'bg-slate-50 text-slate-400 hover:text-blue-600 hover:bg-blue-50'
                            }`}
                          >
                            <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                              <path d={editingId === res.id ? "M5 13l4 4L19 7" : "M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"} />
                            </svg>
                          </button>
                          <button 
                            onClick={() => deleteResource(res.id)}
                            className="p-3 bg-slate-50 text-slate-300 hover:bg-red-50 hover:text-red-500 rounded-2xl transition-all duration-300"
                          >
                            <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                              <path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                            </svg>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : (
          <div className="space-y-10">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">System Collections</h2>
                <p className="text-slate-500 text-[14px]">Define the core organizational structure of the repository.</p>
              </div>
              <button 
                onClick={addCollection}
                className="bg-[#2563EB] text-white px-8 py-4 rounded-full text-[13px] font-[800] uppercase tracking-widest hover:bg-blue-600 transition-all shadow-lg shadow-blue-200 flex items-center gap-3 active:scale-95"
              >
                <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M12 4v16m8-8H4" /></svg>
                New Collection
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {collections.map(col => (
                <div 
                  key={col.id} 
                  className={`p-8 rounded-[32px] border transition-all duration-500 group relative ${
                    editingCollectionId === col.id 
                    ? 'border-blue-300 bg-blue-50/20 shadow-xl scale-[1.02] ring-2 ring-blue-100' 
                    : 'border-slate-100 bg-white hover:border-slate-300 hover:shadow-lg'
                  }`}
                >
                  <div className="flex items-start gap-6">
                    <div className="w-16 h-16 bg-slate-50 rounded-[22px] flex items-center justify-center text-3xl shadow-sm border border-slate-100 group-hover:scale-110 transition-transform">
                      {editingCollectionId === col.id ? (
                        <input 
                            className="w-full text-center bg-transparent outline-none" 
                            value={col.icon} 
                            onChange={(e) => updateCollection(col.id, 'icon', e.target.value)}
                        />
                      ) : col.icon}
                    </div>
                    <div className="flex-1 space-y-3">
                      {editingCollectionId === col.id ? (
                        <>
                          <input 
                            className="w-full text-xl font-[900] text-[#0F172A] border-b-2 border-blue-400 outline-none bg-transparent"
                            value={col.name}
                            onChange={(e) => updateCollection(col.id, 'name', e.target.value)}
                            placeholder="Collection Name"
                          />
                          <textarea 
                            className="w-full text-[13px] text-slate-600 border border-slate-200 rounded-xl p-3 outline-none bg-white mt-2"
                            value={col.description}
                            rows={2}
                            onChange={(e) => updateCollection(col.id, 'description', e.target.value)}
                            placeholder="Describe this collection..."
                          />
                          <div className="space-y-2 mt-4">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest px-1">Sub-Categories (CSV)</label>
                            <input 
                              className="w-full text-[12px] font-bold text-slate-700 bg-white border border-slate-200 rounded-xl p-3 outline-none"
                              value={col.subCategories?.join(', ')}
                              onChange={(e) => updateCollection(col.id, 'subCategories', e.target.value.split(',').map(s => s.trim()))}
                              placeholder="e.g. Markov, PSA, DCE"
                            />
                          </div>
                        </>
                      ) : (
                        <>
                          <div className="flex items-center gap-3">
                            <h4 className="text-[20px] font-[900] text-[#0F172A]">{col.name}</h4>
                            <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest bg-slate-50 px-2 py-0.5 rounded border border-slate-100">{col.id}</span>
                          </div>
                          <p className="text-[14px] text-slate-500 leading-relaxed">{col.description}</p>
                          <div className="flex flex-wrap gap-2 pt-2">
                            {col.subCategories?.map(sub => (
                              <span key={sub} className="px-3 py-1 bg-slate-50 border border-slate-100 rounded-full text-[10px] font-bold text-slate-400 uppercase tracking-widest">
                                {sub}
                              </span>
                            ))}
                          </div>
                        </>
                      )}
                    </div>
                  </div>

                  <div className="absolute top-6 right-6 flex gap-2">
                    <button 
                      onClick={() => setEditingCollectionId(editingCollectionId === col.id ? null : col.id)}
