const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Admin State
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(false);

  // Data State
  const [collections, setCollections] = useState<Collection[]>(initialCollections);
  const [resources, setResources] = useState<Resource[]>(initialResources);
  const [submissions, setSubmissions] = useState<Submission[]>([]);

  // Contribution Modal State
  const [showContributeModal, setShowContributeModal] = useState(false);
  const [contributionData, setContributionData] = useState({ title: '', url: '', description: '', userName: '' });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    if (password === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    if (activeTheme === 'admin-deck' || activeTheme === 'admin-mailbox') {
      setActiveTheme('all');
    }
  };

  const handlePublicContribute = () => {
    const newSubmission: Submission = {
      id: Math.random().toString(36).substr(2, 9),
      ...contributionData,
      submittedDate: new Date().toLocaleDateString('en-GB')
    };
    setSubmissions([newSubmission, ...submissions]);
    setContributionSuccess(true);
    setTimeout(() => {
      setShowContributeModal(false);
      setContributionSuccess(false);
      setContributionData({ title: '', url: '', description: '', userName: '' });
    }, 2000);
  };

  const filteredResources = useMemo(() => {
    return resources.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery, resources]);

  const currentCollection = useMemo(() => 
    collections.find(c => c.id === activeTheme), 
    [activeTheme, collections]
  );

  const isAdminDeckActive = (activeTheme === 'admin-deck' || activeTheme === 'admin-mailbox') && isAdmin;
  const collectionName = activeTheme === 'admin-mailbox' ? 'Mailbox' : (isAdminDeckActive ? 'Management' : (activeTheme === 'all' ? 'General' : currentCollection?.name || 'General'));
