                        <svg className={`w-4 h-4 ${isActive ? 'text-white' : 'text-[#475569]'}`} fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2.5"><rect x="3" y="3" width="7" height="7" rx="1"/><rect x="14" y="3" width="7" height="7" rx="1"/><rect x="14" y="14" width="7" height="7" rx="1"/><rect x="3" y="14" width="7" height="7" rx="1"/></svg>
                      )}
                    </div>
                    <span className="text-[14.5px] font-[800] tracking-tight truncate">{col.name}</span>
                  </button>
                );
              })}
            </div>
          </div>
        </nav>

        <div className="p-4 border-t border-[#E5E7EB]/40">
          <button 
            onClick={onAdminClick}
            className={`w-full flex items-center gap-2.5 px-4 py-3.5 ${isAdminActive ? 'text-red-500 hover:bg-red-50' : 'text-[#94A3B8] hover:bg-slate-50'} cursor-pointer transition-all duration-300 group rounded-[20px]`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="w-[18px] h-[18px] transition-transform group-hover:scale-110" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2.5">
              <path strokeLinecap="round" strokeLinejoin="round" d={isAdminActive ? "M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" : "M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"} />
            </svg>
            <span className="text-[14px] font-[800] tracking-tight antialiased">
              {isAdminActive ? 'Exit Admin Mode' : 'Admin Portal'}
            </span>
          </button>
        </div>
      </aside>
    </>
  );
};

