        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-8 flex items-center justify-between">
          <div className="flex items-center gap-8">
            <span className="text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase">SYSTEM VERSION 2.4.0</span>
            {isAdmin && <span className="text-[10px] font-bold text-[#2563EB] tracking-[0.15em] uppercase">ADMIN PRIVILEGES GRANTED</span>}
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'bg-emerald-500'} animate-pulse`}></div>
             <span className="text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? 'ADMIN MODE' : 'SYNC SECURE'}</span>
          </div>
        </footer>
      </main>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-8 rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-2xl font-[900] text-[#0F172A] mb-2">Admin Portal</h2>
            <p className="text-slate-500 text-sm mb-6">Enter system password to access management tools.</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">Incorrect Password</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
