import AdminDashboard from './components/AdminDashboard';
import { COLLECTIONS as initialCollections, RESOURCES as initialResources } from './data';
import { Resource, Collection, Theme, Submission } from './types';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Admin State
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(false);

  // Data State
  const [collections, setCollections] = useState<Collection[]>(initialCollections);
  const [resources, setResources] = useState<Resource[]>(initialResources);
  const [submissions, setSubmissions] = useState<Submission[]>([]);
  const [taglineWords, setTaglineWords] = useState<string[]>(['HEOR', 'MARKOV', 'PSA']);

  // Contribution Modal State
  const [showContributeModal, setShowContributeModal] = useState(false);
  const [contributionData, setContributionData] = useState({ 
    title: '', 
    url: '', 
    description: '', 
    userName: '', 
    category: 'all', 
    wantsCredit: true 
  });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    if (password === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    if (activeTheme === 'admin-deck') {
      setActiveTheme('all');
    }
  };

  const handlePublicContribute = () => {
    const newSubmission: Submission = {
      id: Math.random().toString(36).substr(2, 9),
      ...contributionData,
      submittedDate: new Date().toLocaleDateString('en-GB')
    };
    setSubmissions([newSubmission, ...submissions]);
    setContributionSuccess(true);
    setTimeout(() => {
      setShowContributeModal(false);
      setContributionSuccess(false);
      setContributionData({ 
        title: '', 
        url: '', 
        description: '', 
        userName: '', 
        category: 'all', 
        wantsCredit: true 
      });
    }, 2000);
  };

  const filteredResources = useMemo(() => {
    return resources.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery, resources]);

  const currentCollection = useMemo(() => 
    collections.find(c => c.id === activeTheme), 
    [activeTheme, collections]
  );

  const isAdminDeckActive = activeTheme === 'admin-deck' && isAdmin;
  const collectionName = isAdminDeckActive ? 'Management' : (activeTheme === 'all' ? 'General' : currentCollection?.name || 'General');
  const subCategories = currentCollection?.subCategories || [];

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter'] antialiased">
      <Sidebar 
        collections={collections} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        onAdminClick={() => isAdmin ? handleLogout() : setShowLoginModal(true)}
        isAdminActive={isAdmin}
        notificationCount={submissions.length}
        taglineWords={taglineWords}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white px-8 pt-8 pb-5 select-none border-b border-[#F1F5F9]/60 relative z-30">
          <div className="max-w-[1440px] mx-auto">
            <div className="flex items-end justify-between gap-8 mb-6">
              <div className="flex flex-col gap-3">
                <div className="flex items-center gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[10px] font-[800] tracking-[0.15em] text-[#2563EB] uppercase antialiased transition-all duration-500">
                    {isAdminDeckActive ? 'ADMIN CONTROL PANEL' : (activeTheme === 'all' ? 'GENERAL' : (currentCollection?.name.toUpperCase() || 'UNKNOWN'))}
                  </div>
                  {!isAdminDeckActive && (
                    <div className="text-[10px] font-[800] tracking-[0.15em] text-[#64748B] uppercase antialiased">
                      {filteredResources.length} ITEMS
                    </div>
                  )}
                </div>
                <h1 className="text-[48px] font-[900] tracking-[-0.04em] masonry-title leading-[0.9] text-[#0F172A] antialiased transition-all duration-500">
                  {collectionName} <span className="text-[#D1D5DB]">{isAdminDeckActive ? 'Deck' : 'Vault'}</span>
                </h1>
              </div>

              <div className="flex items-center gap-6 pb-1">
                {!isAdminDeckActive && (
                  <div className="relative w-[380px]">
                    <div className="absolute inset-y-0 left-0 pl-5 flex items-center pointer-events-none text-[#94A3B8]">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                      </svg>
                    </div>
                    <input
                      type="text"
                      placeholder="Search repository..."
                      className="block w-full pl-12 pr-6 py-3.5 bg-[#F8FAFC] border border-[#F1F5F9] rounded-full text-[15px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#4F46E5] focus:bg-white outline-none shadow-sm transition-all duration-300 placeholder-[#94A3B8]"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>
                )}

                <button 
                  onClick={() => isAdmin ? handleLogout() : setShowContributeModal(true)}
                  className={`flex items-center gap-2.5 px-8 py-3.5 ${isAdminDeckActive ? 'bg-[#0F172A]' : 'bg-[#2563EB]'} text-white text-[15px] font-[700] rounded-full hover:opacity-90 transition-all duration-300 shadow-md active:scale-[0.96]`}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="3">
                    <path strokeLinecap="round" strokeLinejoin="round" d={isAdminDeckActive ? "M15 19l-7-7 7-7" : "M12 4v16m8-8H4"} />
                  </svg>
                  {isAdminDeckActive ? 'Exit Admin View' : 'Contribute'}
                </button>
              </div>
            </div>

            {/* Sub-category Filter Bar */}
            {!isAdminDeckActive && activeTheme !== 'all' && subCategories.length > 0 && (
              <div className="flex items-center gap-3 overflow-x-auto pb-1 scrollbar-hide animate-card-entry">
                <button
                  onClick={() => setActiveSubCategory('all')}
                  className={`px-6 py-2.5 rounded-full text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                    activeSubCategory === 'all'
                    ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                    : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                  }`}
                >
                  All {collectionName}s
                </button>
                {subCategories.map((sub, idx) => (
                  <button
                    key={sub}
                    onClick={() => setActiveSubCategory(sub)}
                    className={`px-6 py-2.5 rounded-full text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                      activeSubCategory === sub
                      ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                      : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                    }`}
                    style={{ transitionDelay: `${idx * 0.02}s` }}
                  >
                    {sub}
                  </button>
                ))}
              </div>
            )}
          </div>
        </header>

        {/* Scrollable Content Area */}
        <div className="flex-1 overflow-y-auto px-8 py-10 scroll-smooth">
          <div className="max-w-[1440px] mx-auto">
            {isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center py-32 text-center animate-card-entry">
                  <div className="w-16 h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-6 shadow-sm">
                    <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                      <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                  <h4 className="text-xl font-[800] text-[#111827] mb-2">Empty Archive</h4>
                  <p className="text-[14px] text-[#6B7280]">Adjust your filters or try a different search term.</p>
                </div>
              )
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-8 flex items-center justify-between">
          <div className="flex items-center gap-8">
            <span className="text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase">SYSTEM VERSION 2.4.0</span>
            {isAdmin && <span className="text-[10px] font-bold text-[#2563EB] tracking-[0.15em] uppercase">ADMIN PRIVILEGES GRANTED</span>}
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'bg-emerald-500'} animate-pulse`}></div>
             <span className="text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? 'ADMIN MODE' : 'SYNC SECURE'}</span>
          </div>
        </footer>
      </main>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-8 rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-2xl font-[900] text-[#0F172A] mb-2">Admin Portal</h2>
            <p className="text-slate-500 text-sm mb-6">Enter system password to access management tools.</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">Incorrect Password</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                <button onClick={handleLogin} className="flex-[2] py-3.5 rounded-2xl bg-[#0F172A] text-white text-[13px] font-bold hover:bg-black shadow-lg">Confirm Access</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Public Contribution Modal */}
      {showContributeModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-8 rounded-[32px] shadow-2xl w-full max-w-[500px] border border-slate-100 overflow-y-auto max-h-[90vh]">
            {contributionSuccess ? (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-8 h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                </div>
                <h3 className="text-xl font-black text-[#0F172A] mb-2">Submission Received</h3>
                <p className="text-slate-500 text-sm">Thank you! Our curators will review your contribution shortly.</p>
              </div>
            ) : (
              <>
                <h2 className="text-2xl font-[900] text-[#0F172A] mb-2">Contribute Resource</h2>
                <p className="text-slate-500 text-sm mb-6">Suggest a high-quality HEOR resource to the repository.</p>
                <div className="space-y-4">
                  <div>
                    <label className="text-[11px] font-black text-slate-400 uppercase tracking-widest block mb-2 ml-1">Title</label>
                    <input 
                      className="w-full px-5 py-3 rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20"
                      placeholder="Resource Title"
                      value={contributionData.title}
                      onChange={(e) => setContributionData({...contributionData, title: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[11px] font-black text-slate-400 uppercase tracking-widest block mb-2 ml-1">URL</label>
                    <input 
                      className="w-full px-5 py-3 rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20"
                      placeholder="URL (https://...)"
                      value={contributionData.url}
                      onChange={(e) => setContributionData({...contributionData, url: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[11px] font-black text-slate-400 uppercase tracking-widest block mb-2 ml-1">Suggested Collection (Optional)</label>
                    <select 
                      className="w-full px-5 py-3 rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 appearance-none cursor-pointer"
                      value={contributionData.category}
                      onChange={(e) => setContributionData({...contributionData, category: e.target.value})}
                    >
                      <option value="all">General / Not Specified</option>
                      {collections.map(col => (
                        <option key={col.id} value={col.id}>{col.name}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="text-[11px] font-black text-slate-400 uppercase tracking-widest block mb-2 ml-1">Description</label>
                    <textarea 
                      className="w-full px-5 py-3 rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 min-h-[100px]"
                      placeholder="Brief description of the resource..."
                      value={contributionData.description}
                      onChange={(e) => setContributionData({...contributionData, description: e.target.value})}
                    />
                  </div>
                  <div className="flex flex-col gap-4">
                    <div>
                      <label className="text-[11px] font-black text-slate-400 uppercase tracking-widest block mb-2 ml-1">Your Name</label>
                      <input 
                        className="w-full px-5 py-3 rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20"
                        placeholder="Your Name"
                        value={contributionData.userName}
                        onChange={(e) => setContributionData({...contributionData, userName: e.target.value})}
                      />
                    </div>
                    <label className="flex items-center gap-3 group cursor-pointer select-none py-1">
                      <div className={`w-5 h-5 rounded border-2 transition-all flex items-center justify-center ${contributionData.wantsCredit ? 'bg-[#2563EB] border-[#2563EB]' : 'border-slate-300'}`}>
                        {contributionData.wantsCredit && <svg className="w-3.5 h-3.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={4}><path d="M5 13l4 4L19 7" /></svg>}
                      </div>
                      <input 
                        type="checkbox" 
                        className="hidden" 
                        checked={contributionData.wantsCredit} 
                        onChange={() => setContributionData({...contributionData, wantsCredit: !contributionData.wantsCredit})}
                      />
                      <span className="text-sm font-bold text-slate-600">Show my name as contributor</span>
                    </label>
                  </div>
                  <div className="flex gap-3 pt-2">
                    <button onClick={() => setShowContributeModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                    <button onClick={handlePublicContribute} className="flex-[2] py-3.5 rounded-2xl bg-[#2563EB] text-white text-[13px] font-bold hover:bg-blue-600 shadow-lg active:scale-95 transition-all">Submit to Review</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingSubId, setEditingSubId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // Resource Management Logic
  const updateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (window.confirm('Delete this resource permanently?')) {
      setResources(prev => prev.filter(res => res.id !== id));
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'Describe this resource...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management Logic
  const updateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => col.id === id ? { ...col, [field]: value } : col));
  };

  const addCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    setCollections([...collections, { id: newId, name: 'New Collection', icon: '', description: '', subCategories: ['General'] }]);
  };

  // Mailbox Logic
  const updateSubmission = (id: string, field: keyof Submission, value: any) => {
    setSubmissions(prev => prev.map(s => s.id === id ? { ...s, [field]: value } : s));
  };

  const approveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };
      setResources([newRes, ...resources]);
      setSubmissions(submissions.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
      setEditingSubId(null);
    } catch (e) {
      alert("Invalid URL. Please check the URL format before approving.");
    }
  };

  const rejectSubmission = (id: string) => {
    if (window.confirm('Reject this submission?')) {
      setSubmissions(submissions.filter(s => s.id !== id));
      if (editingSubId === id) setEditingSubId(null);
    }
  };

  // Tagline Logic
  const addTaglineWord = () => {
    if (newTaglineWord.trim()) {
      setTaglineWords([...taglineWords, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const removeTaglineWord = (index: number) => {
    if (taglineWords.length > 1) {
      setTaglineWords(taglineWords.filter((_, i) => i !== index));
    } else {
      alert("At least one word is required for the typing effect.");
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        <button onClick={() => setActiveTab('resources')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'resources' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500'}`}>Resources</button>
        <button onClick={() => setActiveTab('collections')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'collections' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500'}`}>Collections</button>
        <button onClick={() => setActiveTab('mailbox')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 relative whitespace-nowrap ${activeTab === 'mailbox' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500'}`}>
          Mailbox
          {submissions.length > 0 && <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full">{submissions.length}</span>}
        </button>
        <button onClick={() => setActiveTab('settings')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'settings' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500'}`}>Settings</button>
      </div>

      <div className="p-10">
        {activeTab === 'mailbox' ? (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">User Submissions</h2>
            {submissions.length === 0 ? (
              <div className="py-20 text-center text-slate-400">
                <svg className="w-16 h-16 mx-auto mb-4 opacity-20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" /></svg>
                <p className="font-bold uppercase tracking-widest text-[11px]">No pending submissions</p>
              </div>
            ) : (
              <div className="space-y-6">
                {submissions.map(sub => {
                  const isEditingSub = editingSubId === sub.id;
                  return (
                    <div key={sub.id} className={`p-6 rounded-3xl border transition-all flex flex-col gap-6 shadow-sm ${isEditingSub ? 'border-blue-300 bg-blue-50/20 ring-4 ring-blue-50' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                      <div className="flex-1 space-y-4">
                        <div className="flex items-center gap-3">
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest bg-blue-50 px-2 py-1 rounded">SUBMISSION</span>
                          {isEditingSub ? (
                            <input 
                              className="flex-1 text-[18px] font-black text-[#0F172A] bg-white border-b border-blue-400 outline-none px-2 py-1"
                              value={sub.title}
                              onChange={e => updateSubmission(sub.id, 'title', e.target.value)}
                            />
                          ) : (
                            <h4 className="text-[18px] font-black text-[#0F172A]">{sub.title}</h4>
                          )}
                        </div>

                        {isEditingSub ? (
                          <textarea 
                            className="w-full text-slate-500 text-sm leading-relaxed bg-white border border-slate-200 rounded-xl p-3 outline-none focus:ring-2 focus:ring-blue-500/20"
                            value={sub.description}
                            onChange={e => updateSubmission(sub.id, 'description', e.target.value)}
                            rows={3}
                          />
                        ) : (
                          <p className="text-slate-500 text-sm leading-relaxed">{sub.description}</p>
                        )}

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-1">
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Submitter & Credit</span>
                            {isEditingSub ? (
                              <div className="flex items-center gap-2">
                                <input 
                                  className="flex-1 text-[12px] font-bold text-slate-800 bg-white border-b border-slate-200 py-1 outline-none"
                                  value={sub.userName}
                                  onChange={e => updateSubmission(sub.id, 'userName', e.target.value)}
                                />
                                <button 
                                  onClick={() => updateSubmission(sub.id, 'wantsCredit', !sub.wantsCredit)}
                                  className={`text-[9px] font-black px-2 py-1 rounded uppercase tracking-tighter border transition-all ${sub.wantsCredit ? 'bg-blue-500 text-white border-blue-500' : 'bg-white text-slate-400 border-slate-200'}`}
                                >
                                  {sub.wantsCredit ? 'Credited' : 'Anonymous'}
                                </button>
                              </div>
                            ) : (
                              <span className="text-[12px] font-bold text-slate-800">{sub.userName} {!sub.wantsCredit && <span className="text-slate-400 font-medium">(Anonymous Pref)</span>}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Target Collection</span>
                            {isEditingSub ? (
                              <select 
                                className="text-[12px] font-bold text-[#2563EB] bg-white border-b border-slate-200 py-1 outline-none uppercase cursor-pointer"
                                value={sub.category}
                                onChange={e => updateSubmission(sub.id, 'category', e.target.value)}
                              >
                                <option value="all">General</option>
                                {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                              </select>
                            ) : (
                              <span className="text-[12px] font-bold text-[#2563EB] uppercase">{collections.find(c => c.id === sub.category)?.name || 'General'}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1 md:col-span-2">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">URL Source</span>
                            {isEditingSub ? (
                              <input 
                                className="text-[12px] font-bold text-blue-500 bg-white border-b border-slate-200 py-1 outline-none"
                                value={sub.url}
                                onChange={e => updateSubmission(sub.id, 'url', e.target.value)}
                              />
                            ) : (
                              <span className="text-[12px] font-bold text-blue-500 truncate">{sub.url}</span>
                            )}
                          </div>
                        </div>
                      </div>
                      <div className="flex gap-2 justify-end items-center pt-2 border-t border-slate-50">
                        <button 
                          onClick={() => setEditingSubId(isEditingSub ? null : sub.id)} 
                          className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditingSub ? 'bg-[#0F172A] text-white' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}
                        >
                          <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                          {isEditingSub ? 'Done Modifying' : 'Modify'}
                        </button>
                        <button 
                          onClick={() => approveSubmission(sub)} 
                          className="flex items-center gap-2 px-8 py-2.5 bg-emerald-500 text-white rounded-xl text-[12px] font-black uppercase tracking-widest hover:bg-emerald-600 shadow-lg active:scale-95 transition-all"
                        >
                          <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                          Approve {isEditingSub && '& Save'}
                        </button>
                        <button 
                          onClick={() => rejectSubmission(sub.id)} 
                          className="px-4 py-2.5 text-slate-400 rounded-xl text-[12px] font-bold hover:text-red-500 transition-colors"
                        >
                          Reject
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        ) : activeTab === 'resources' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Active Vault</h2>
              <button onClick={addResource} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">New Entry</button>
            </div>
            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm">
              <table className="w-full text-left border-collapse">
                <thead className="bg-[#F8FAFC] border-b border-slate-200"><tr className="text-[11px] font-black uppercase tracking-widest text-slate-400"><th className="px-8 py-4">Resource</th><th className="px-8 py-4">Status</th><th className="px-8 py-4 text-right">Action</th></tr></thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => (
                    <tr key={res.id} className={`group ${editingId === res.id ? 'bg-blue-50/30' : 'hover:bg-slate-50/50'}`}>
                      <td className="px-8 py-5">
                        {editingId === res.id ? (
                          <div className="space-y-2">
                            <input className="w-full font-black text-[#0F172A] outline-none border-b border-blue-400 py-1" value={res.title} onChange={e => updateResource(res.id, 'title', e.target.value)} />
                            <input className="w-full text-xs text-slate-400 outline-none" value={res.url} onChange={e => updateResource(res.id, 'url', e.target.value)} />
                          </div>
                        ) : (
                          <div><div className="font-bold text-[#0F172A]">{res.title}</div><div className="text-[11px] text-slate-400">{res.domain}</div></div>
                        )}
                      </td>
                      <td className="px-8 py-5">
                        <span className="text-[10px] font-black px-2 py-1 rounded bg-slate-100 uppercase tracking-widest">{res.category}</span>
                      </td>
                      <td className="px-8 py-5 text-right flex justify-end gap-2">
                         <button onClick={() => setEditingId(editingId === res.id ? null : res.id)} className={`p-2 rounded-xl transition-all ${editingId === res.id ? 'bg-blue-500 text-white' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}>Edit</button>
                         <button onClick={() => deleteResource(res.id)} className="p-2 bg-slate-50 text-slate-400 hover:text-red-500 transition-colors">Del</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : activeTab === 'collections' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Collections</h2>
              <button onClick={addCollection} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">Add Collection</button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {collections.map(col => (
                <div key={col.id} className="p-6 rounded-[28px] border border-slate-100 bg-white flex items-center justify-between group hover:border-blue-200 transition-all shadow-sm">
                  <div className="flex items-center gap-4">
                    <div className="text-2xl w-12 h-12 flex items-center justify-center bg-slate-50 rounded-2xl group-hover:bg-blue-50 transition-colors">{col.icon}</div>
                    <div className="font-black text-[#0F172A]">{col.name}</div>
                  </div>
                  <button onClick={() => setCollections(collections.filter(c => c.id !== col.id))} className="text-slate-300 hover:text-red-500 transition-colors">Remove</button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">System Settings</h2>

            <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100">
              <div className="flex flex-col gap-6">
                <div>
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Typing Tagline Config</h3>
                  <p className="text-slate-500 text-sm mb-6">Manage the list of words that cycle in the sidebar typing animation.</p>

                  <div className="flex flex-wrap gap-3 mb-6">
                    {taglineWords.map((word, idx) => (
                      <div key={idx} className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-slate-200 shadow-sm group">
                        <span className="text-[14px] font-bold text-slate-700">{word}</span>
                        <button 
                          onClick={() => removeTaglineWord(idx)}
                          className="text-slate-300 hover:text-red-500 transition-colors"
                        >
                          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M6 18L18 6M6 6l12 12" /></svg>
                        </button>
                      </div>
                    ))}
                  </div>

                  <div className="flex gap-3 max-w-md">
                    <input 
                      type="text" 
                      placeholder="Add new word..."
                      className="flex-1 px-5 py-3 rounded-2xl bg-white border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 shadow-sm"
                      value={newTaglineWord}
                      onChange={(e) => setNewTaglineWord(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && addTaglineWord()}
                    />
                    <button 
                      onClick={addTaglineWord}
                      className="px-6 py-3 bg-[#0F172A] text-white rounded-2xl text-[13px] font-bold hover:bg-black transition-all active:scale-95 shadow-md"
                    >
                      Add
                    </button>
                  </div>
                </div>

                <div className="pt-6 border-t border-slate-200">
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Global Site Settings</h3>
                  <p className="text-slate-500 text-sm mb-4">Core repository configuration and visibility toggles.</p>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Maintenance Mode</div>
                          <div className="text-xs text-slate-400">Lock the site for all public users</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Auto-Approve Contributions</div>
                          <div className="text-xs text-slate-400">Skip the moderation mailbox queue</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingSubId, setEditingSubId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // Resource Management Logic
  const updateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (window.confirm(' PERMANENT DELETE\n\nAre you sure you want to remove this resource from the repository? This action cannot be undone.')) {
      setResources(prev => prev.filter(res => res.id !== id));
      if (editingId === id) setEditingId(null);
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'Describe this resource...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management Logic
  const removeCollection = (id: string, name: string) => {
    if (window.confirm(` REMOVE COLLECTION: "${name.toUpperCase()}"\n\nDeleting a collection will not delete the associated resources, but they will become uncategorized. Continue?`)) {
      setCollections(prev => prev.filter(c => c.id !== id));
    }
  };

  const addCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    setCollections([...collections, { id: newId, name: 'New Collection', icon: '', description: '', subCategories: ['General'] }]);
  };

  // Mailbox Logic
  const updateSubmission = (id: string, field: keyof Submission, value: any) => {
    setSubmissions(prev => prev.map(s => s.id === id ? { ...s, [field]: value } : s));
  };

  const approveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };
      setResources([newRes, ...resources]);
      setSubmissions(submissions.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
      setEditingSubId(null);
    } catch (e) {
      alert("Invalid URL. Please check the URL format before approving.");
    }
  };

  const rejectSubmission = (id: string) => {
    if (window.confirm(' REJECT SUBMISSION\n\nDiscard this user suggestion? This will remove it from the mailbox permanently.')) {
      setSubmissions(submissions.filter(s => s.id !== id));
      if (editingSubId === id) setEditingSubId(null);
    }
  };

  // Tagline Logic
  const addTaglineWord = () => {
    if (newTaglineWord.trim()) {
      setTaglineWords([...taglineWords, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const removeTaglineWord = (index: number) => {
    if (taglineWords.length > 1) {
      if (window.confirm(`Remove "${taglineWords[index]}" from the typing cycle?`)) {
        setTaglineWords(taglineWords.filter((_, i) => i !== index));
      }
    } else {
      alert("At least one word is required for the typing effect.");
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        <button onClick={() => setActiveTab('resources')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'resources' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Resources</button>
        <button onClick={() => setActiveTab('collections')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'collections' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Collections</button>
        <button onClick={() => setActiveTab('mailbox')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 relative whitespace-nowrap ${activeTab === 'mailbox' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>
          Mailbox
          {submissions.length > 0 && <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>}
        </button>
        <button onClick={() => setActiveTab('settings')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'settings' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Settings</button>
      </div>

      <div className="p-10">
        {activeTab === 'mailbox' ? (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">User Submissions</h2>
            {submissions.length === 0 ? (
              <div className="py-20 text-center text-slate-400">
                <svg className="w-16 h-16 mx-auto mb-4 opacity-20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2-2v10a2 2 0 002 2z" /></svg>
                <p className="font-bold uppercase tracking-widest text-[11px]">No pending submissions</p>
              </div>
            ) : (
              <div className="space-y-6">
                {submissions.map(sub => {
                  const isEditingSub = editingSubId === sub.id;
                  return (
                    <div key={sub.id} className={`p-6 rounded-3xl border transition-all flex flex-col gap-6 shadow-sm ${isEditingSub ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                      <div className="flex-1 space-y-4">
                        <div className="flex items-center gap-3">
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest bg-blue-50 px-2 py-1 rounded">SUBMISSION</span>
                          {isEditingSub ? (
                            <input 
                              className="flex-1 text-[18px] font-black text-[#0F172A] bg-white border-b-2 border-blue-400 outline-none px-2 py-1"
                              value={sub.title}
                              onChange={e => updateSubmission(sub.id, 'title', e.target.value)}
                              placeholder="Edit Title"
                            />
                          ) : (
                            <h4 className="text-[18px] font-black text-[#0F172A]">{sub.title}</h4>
                          )}
                        </div>

                        {isEditingSub ? (
                          <textarea 
                            className="w-full text-slate-500 text-sm leading-relaxed bg-white border border-slate-200 rounded-xl p-3 outline-none focus:ring-2 focus:ring-blue-500/20"
                            value={sub.description}
                            onChange={e => updateSubmission(sub.id, 'description', e.target.value)}
                            rows={3}
                            placeholder="Edit description..."
                          />
                        ) : (
                          <p className="text-slate-500 text-sm leading-relaxed">{sub.description}</p>
                        )}

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-1">
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Submitter & Credit</span>
                            {isEditingSub ? (
                              <div className="flex items-center gap-2">
                                <input 
                                  className="flex-1 text-[12px] font-bold text-slate-800 bg-white border-b border-slate-200 py-1 outline-none"
                                  value={sub.userName}
                                  onChange={e => updateSubmission(sub.id, 'userName', e.target.value)}
                                />
                                <button 
                                  onClick={() => updateSubmission(sub.id, 'wantsCredit', !sub.wantsCredit)}
                                  className={`text-[9px] font-black px-2 py-1 rounded uppercase tracking-tighter border transition-all ${sub.wantsCredit ? 'bg-blue-600 text-white border-blue-600' : 'bg-white text-slate-400 border-slate-200'}`}
                                >
                                  {sub.wantsCredit ? 'Credited' : 'Anonymous'}
                                </button>
                              </div>
                            ) : (
                              <span className="text-[12px] font-bold text-slate-800">{sub.userName} {!sub.wantsCredit && <span className="text-slate-400 font-medium">(Anonymous Pref)</span>}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Target Collection</span>
                            {isEditingSub ? (
                              <select 
                                className="text-[12px] font-bold text-[#2563EB] bg-white border-b border-slate-200 py-1 outline-none uppercase cursor-pointer"
                                value={sub.category}
                                onChange={e => updateSubmission(sub.id, 'category', e.target.value)}
                              >
                                <option value="all">General</option>
                                {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                              </select>
                            ) : (
                              <span className="text-[12px] font-bold text-[#2563EB] uppercase">{collections.find(c => c.id === sub.category)?.name || 'General'}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1 md:col-span-2">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">URL Source</span>
                            {isEditingSub ? (
                              <input 
                                className="text-[12px] font-bold text-blue-500 bg-white border-b border-slate-200 py-1 outline-none w-full"
                                value={sub.url}
                                onChange={e => updateSubmission(sub.id, 'url', e.target.value)}
                              />
                            ) : (
                              <span className="text-[12px] font-bold text-blue-500 truncate block">{sub.url}</span>
                            )}
                          </div>
                        </div>
                      </div>
                      <div className="flex gap-2 justify-end items-center pt-2 border-t border-slate-50">
                        <button 
                          onClick={() => setEditingSubId(isEditingSub ? null : sub.id)} 
                          className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditingSub ? 'bg-emerald-50 text-emerald-600 border border-emerald-100 shadow-sm' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}
                        >
                          {isEditingSub ? (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                              Save Draft
                            </>
                          ) : (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                              Modify
                            </>
                          )}
                        </button>
                        <button 
                          onClick={() => approveSubmission(sub)} 
                          className="flex items-center gap-2 px-8 py-2.5 bg-emerald-500 text-white rounded-xl text-[12px] font-black uppercase tracking-widest hover:bg-emerald-600 shadow-lg active:scale-95 transition-all"
                        >
                          <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                          Approve {isEditingSub && '& Publish'}
                        </button>
                        <button 
                          onClick={() => rejectSubmission(sub.id)} 
                          className="px-4 py-2.5 text-slate-400 rounded-xl text-[12px] font-bold hover:text-red-500 hover:bg-red-50 transition-all"
                        >
                          Reject
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        ) : activeTab === 'resources' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Active Vault</h2>
              <button onClick={addResource} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">New Entry</button>
            </div>
            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm">
              <table className="w-full text-left border-collapse">
                <thead className="bg-[#F8FAFC] border-b border-slate-200"><tr className="text-[11px] font-black uppercase tracking-widest text-slate-400"><th className="px-8 py-4">Resource</th><th className="px-8 py-4">Status</th><th className="px-8 py-4 text-right">Action</th></tr></thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => (
                    <tr key={res.id} className={`group transition-colors ${editingId === res.id ? 'bg-blue-50/40' : 'hover:bg-slate-50/50'}`}>
                      <td className="px-8 py-5">
                        {editingId === res.id ? (
                          <div className="space-y-2 max-w-md">
                            <input className="w-full font-black text-[#0F172A] outline-none border-b-2 border-blue-400 bg-transparent py-1" value={res.title} onChange={e => updateResource(res.id, 'title', e.target.value)} placeholder="Resource Title" />
                            <input className="w-full text-xs text-blue-500 font-bold outline-none bg-transparent" value={res.url} onChange={e => updateResource(res.id, 'url', e.target.value)} placeholder="https://..." />
                          </div>
                        ) : (
                          <div><div className="font-bold text-[#0F172A] group-hover:text-blue-600 transition-colors">{res.title}</div><div className="text-[11px] text-slate-400 font-medium">{res.domain}</div></div>
                        )}
                      </td>
                      <td className="px-8 py-5">
                        <span className="text-[10px] font-black px-2 py-1 rounded bg-slate-100 text-slate-500 uppercase tracking-widest">{res.category}</span>
                      </td>
                      <td className="px-8 py-5 text-right flex justify-end gap-2">
                         <button 
                           onClick={() => setEditingId(editingId === res.id ? null : res.id)} 
                           className={`flex items-center gap-1.5 px-4 py-2 rounded-xl text-[12px] font-bold transition-all ${editingId === res.id ? 'bg-emerald-500 text-white shadow-md' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                         >
                           {editingId === res.id ? (
                             <>
                               <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                               Save
                             </>
                           ) : (
                             <>
                               <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                               Edit
                             </>
                           )}
                         </button>
                         <button onClick={() => deleteResource(res.id)} className="p-2 bg-slate-50 text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all rounded-xl">
                           <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                         </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : activeTab === 'collections' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Collections</h2>
              <button onClick={addCollection} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">Add Collection</button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {collections.map(col => (
                <div key={col.id} className="p-6 rounded-[28px] border border-slate-100 bg-white flex items-center justify-between group hover:border-blue-200 hover:shadow-md transition-all">
                  <div className="flex items-center gap-4">
                    <div className="text-2xl w-12 h-12 flex items-center justify-center bg-slate-50 rounded-2xl group-hover:bg-blue-50 transition-colors shadow-sm">{col.icon}</div>
                    <div className="font-black text-[#0F172A] tracking-tight">{col.name}</div>
                  </div>
                  <button 
                    onClick={() => removeCollection(col.id, col.name)} 
                    className="p-2 text-slate-300 hover:text-red-500 hover:bg-red-50 rounded-xl transition-all"
                    title="Remove Collection"
                  >
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                  </button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">System Settings</h2>

            <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100">
              <div className="flex flex-col gap-6">
                <div>
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Typing Tagline Config</h3>
                  <p className="text-slate-500 text-sm mb-6">Manage the list of words that cycle in the sidebar typing animation.</p>

                  <div className="flex flex-wrap gap-3 mb-6">
                    {taglineWords.map((word, idx) => (
                      <div key={idx} className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-slate-200 shadow-sm group hover:border-blue-300 transition-all">
                        <span className="text-[14px] font-bold text-slate-700">{word}</span>
                        <button 
                          onClick={() => removeTaglineWord(idx)}
                          className="text-slate-300 hover:text-red-500 transition-colors"
                        >
                          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M6 18L18 6M6 6l12 12" /></svg>
                        </button>
                      </div>
                    ))}
                  </div>

                  <div className="flex gap-3 max-w-md">
                    <input 
                      type="text" 
                      placeholder="Add new word..."
                      className="flex-1 px-5 py-3 rounded-2xl bg-white border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 shadow-sm"
                      value={newTaglineWord}
                      onChange={(e) => setNewTaglineWord(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && addTaglineWord()}
                    />
                    <button 
                      onClick={addTaglineWord}
                      className="px-6 py-3 bg-[#0F172A] text-white rounded-2xl text-[13px] font-bold hover:bg-black transition-all active:scale-95 shadow-md flex items-center gap-2"
                    >
                      <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M12 4v16m8-8H4" /></svg>
                      Add
                    </button>
                  </div>
                </div>

                <div className="pt-6 border-t border-slate-200">
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Global Site Settings</h3>
                  <p className="text-slate-500 text-sm mb-4">Core repository configuration and visibility toggles.</p>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed group">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Maintenance Mode</div>
                          <div className="text-xs text-slate-400">Lock the site for all public users</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed group">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Auto-Approve Contributions</div>
                          <div className="text-xs text-slate-400">Skip the moderation mailbox queue</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingSubId, setEditingSubId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // Resource Management Logic
  const updateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (window.confirm(' PERMANENT DELETE\n\nAre you sure you want to remove this resource from the repository? This action cannot be undone.')) {
      setResources(prev => prev.filter(res => res.id !== id));
      if (editingId === id) setEditingId(null);
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'Describe this resource...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management Logic
  const removeCollection = (id: string, name: string) => {
    if (window.confirm(` REMOVE COLLECTION: "${name.toUpperCase()}"\n\nDeleting a collection will not delete the associated resources, but they will become uncategorized. Continue?`)) {
      setCollections(prev => prev.filter(c => c.id !== id));
    }
  };

  const addCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    setCollections([...collections, { id: newId, name: 'New Collection', icon: '', description: '', subCategories: ['General'] }]);
  };

  // Mailbox Logic
  const updateSubmission = (id: string, field: keyof Submission, value: any) => {
    setSubmissions(prev => prev.map(s => s.id === id ? { ...s, [field]: value } : s));
  };

  const approveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };
      setResources([newRes, ...resources]);
      setSubmissions(submissions.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
      setEditingSubId(null);
    } catch (e) {
      alert("Invalid URL. Please check the URL format before approving.");
    }
  };

  const rejectSubmission = (id: string) => {
    if (window.confirm(' REJECT SUBMISSION\n\nDiscard this user suggestion? This will remove it from the mailbox permanently.')) {
      setSubmissions(submissions.filter(s => s.id !== id));
      if (editingSubId === id) setEditingSubId(null);
    }
  };

  // Tagline Logic
  const addTaglineWord = () => {
    if (newTaglineWord.trim()) {
      setTaglineWords([...taglineWords, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const removeTaglineWord = (index: number) => {
    if (taglineWords.length > 1) {
      if (window.confirm(`Remove "${taglineWords[index]}" from the typing cycle?`)) {
        setTaglineWords(taglineWords.filter((_, i) => i !== index));
      }
    } else {
      alert("At least one word is required for the typing effect.");
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        <button onClick={() => setActiveTab('resources')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'resources' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Resources</button>
        <button onClick={() => setActiveTab('collections')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'collections' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Collections</button>
        <button onClick={() => setActiveTab('mailbox')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 relative whitespace-nowrap ${activeTab === 'mailbox' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>
          Mailbox
          {submissions.length > 0 && <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>}
        </button>
        <button onClick={() => setActiveTab('settings')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'settings' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Settings</button>
      </div>

      <div className="p-10">
        {activeTab === 'mailbox' ? (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">User Submissions</h2>
            {submissions.length === 0 ? (
              <div className="py-20 text-center text-slate-400">
                <svg className="w-16 h-16 mx-auto mb-4 opacity-20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" /></svg>
                <p className="font-bold uppercase tracking-widest text-[11px]">No pending submissions</p>
              </div>
            ) : (
              <div className="space-y-6">
                {submissions.map(sub => {
                  const isEditingSub = editingSubId === sub.id;
                  return (
                    <div key={sub.id} className={`p-6 rounded-3xl border transition-all flex flex-col gap-6 shadow-sm ${isEditingSub ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                      <div className="flex-1 space-y-4">
                        <div className="flex items-center gap-3">
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest bg-blue-50 px-2 py-1 rounded">SUBMISSION</span>
                          {isEditingSub ? (
                            <input 
                              className="flex-1 text-[18px] font-black text-[#0F172A] bg-white border-b-2 border-blue-400 outline-none px-2 py-1"
                              value={sub.title}
                              onChange={e => updateSubmission(sub.id, 'title', e.target.value)}
                              placeholder="Edit Title"
                            />
                          ) : (
                            <h4 className="text-[18px] font-black text-[#0F172A]">{sub.title}</h4>
                          )}
                        </div>

                        {isEditingSub ? (
                          <textarea 
                            className="w-full text-slate-500 text-sm leading-relaxed bg-white border border-slate-200 rounded-xl p-3 outline-none focus:ring-2 focus:ring-blue-500/20"
                            value={sub.description}
                            onChange={e => updateSubmission(sub.id, 'description', e.target.value)}
                            rows={3}
                            placeholder="Edit description..."
                          />
                        ) : (
                          <p className="text-slate-500 text-sm leading-relaxed">{sub.description}</p>
                        )}

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-1">
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Submitter & Credit</span>
                            {isEditingSub ? (
                              <div className="flex items-center gap-2">
                                <input 
                                  className="flex-1 text-[12px] font-bold text-slate-800 bg-white border-b border-slate-200 py-1 outline-none"
                                  value={sub.userName}
                                  onChange={e => updateSubmission(sub.id, 'userName', e.target.value)}
                                />
                                <button 
                                  onClick={() => updateSubmission(sub.id, 'wantsCredit', !sub.wantsCredit)}
                                  className={`text-[9px] font-black px-2 py-1 rounded uppercase tracking-tighter border transition-all ${sub.wantsCredit ? 'bg-blue-600 text-white border-blue-600' : 'bg-white text-slate-400 border-slate-200'}`}
                                >
                                  {sub.wantsCredit ? 'Credited' : 'Anonymous'}
                                </button>
                              </div>
                            ) : (
                              <span className="text-[12px] font-bold text-slate-800">{sub.userName} {!sub.wantsCredit && <span className="text-slate-400 font-medium">(Anonymous Pref)</span>}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Target Collection</span>
                            {isEditingSub ? (
                              <select 
                                className="text-[12px] font-bold text-[#2563EB] bg-white border-b border-slate-200 py-1 outline-none uppercase cursor-pointer"
                                value={sub.category}
                                onChange={e => updateSubmission(sub.id, 'category', e.target.value)}
                              >
                                <option value="all">General</option>
                                {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                              </select>
                            ) : (
                              <span className="text-[12px] font-bold text-[#2563EB] uppercase">{collections.find(c => c.id === sub.category)?.name || 'General'}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1 md:col-span-2">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">URL Source</span>
                            {isEditingSub ? (
                              <input 
                                className="text-[12px] font-bold text-blue-500 bg-white border-b border-slate-200 py-1 outline-none w-full"
                                value={sub.url}
                                onChange={e => updateSubmission(sub.id, 'url', e.target.value)}
                              />
                            ) : (
                              <span className="text-[12px] font-bold text-blue-500 truncate block">{sub.url}</span>
                            )}
                          </div>
                        </div>
                      </div>
                      <div className="flex gap-2 justify-end items-center pt-2 border-t border-slate-50">
                        <button 
                          onClick={() => setEditingSubId(isEditingSub ? null : sub.id)} 
                          className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditingSub ? 'bg-emerald-50 text-emerald-600 border border-emerald-100 shadow-sm' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}
                        >
                          {isEditingSub ? (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                              Save Draft
                            </>
                          ) : (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                              Modify
                            </>
                          )}
                        </button>
                        <button 
                          onClick={() => approveSubmission(sub)} 
                          className="flex items-center gap-2 px-8 py-2.5 bg-emerald-500 text-white rounded-xl text-[12px] font-black uppercase tracking-widest hover:bg-emerald-600 shadow-lg active:scale-95 transition-all"
                        >
                          <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                          Approve {isEditingSub && '& Publish'}
                        </button>
                        <button 
                          onClick={() => rejectSubmission(sub.id)} 
                          className="px-4 py-2.5 text-slate-400 rounded-xl text-[12px] font-bold hover:text-red-500 hover:bg-red-50 transition-all"
                        >
                          Reject
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        ) : activeTab === 'resources' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Active Vault</h2>
              <button onClick={addResource} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">New Entry</button>
            </div>
            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm">
              <table className="w-full text-left border-collapse">
                <thead className="bg-[#F8FAFC] border-b border-slate-200"><tr className="text-[11px] font-black uppercase tracking-widest text-slate-400"><th className="px-8 py-4">Resource</th><th className="px-8 py-4">Collection</th><th className="px-8 py-4 text-right">Action</th></tr></thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => (
                    <tr key={res.id} className={`group transition-colors ${editingId === res.id ? 'bg-blue-50/40' : 'hover:bg-slate-50/50'}`}>
                      <td className="px-8 py-5">
                        {editingId === res.id ? (
                          <div className="space-y-2 max-w-md">
                            <input className="w-full font-black text-[#0F172A] outline-none border-b-2 border-blue-400 bg-transparent py-1" value={res.title} onChange={e => updateResource(res.id, 'title', e.target.value)} placeholder="Resource Title" />
                            <input className="w-full text-xs text-blue-500 font-bold outline-none bg-transparent" value={res.url} onChange={e => updateResource(res.id, 'url', e.target.value)} placeholder="https://..." />
                          </div>
                        ) : (
                          <div><div className="font-bold text-[#0F172A] group-hover:text-blue-600 transition-colors">{res.title}</div><div className="text-[11px] text-slate-400 font-medium">{res.domain}</div></div>
                        )}
                      </td>
                      <td className="px-8 py-5">
                        {editingId === res.id ? (
                          <select 
                            className="text-[10px] font-black px-2 py-1 rounded bg-white border border-blue-200 text-blue-600 uppercase tracking-widest outline-none focus:ring-2 focus:ring-blue-100"
                            value={res.category}
                            onChange={e => updateResource(res.id, 'category', e.target.value)}
                          >
                            <option value="all">General</option>
                            {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                          </select>
                        ) : (
                          <span className="text-[10px] font-black px-2 py-1 rounded bg-slate-100 text-slate-500 uppercase tracking-widest">
                            {collections.find(c => c.id === res.category)?.name || 'General'}
                          </span>
                        )}
                      </td>
                      <td className="px-8 py-5 text-right flex justify-end gap-2">
                         <button 
                           onClick={() => setEditingId(editingId === res.id ? null : res.id)} 
                           className={`flex items-center gap-1.5 px-4 py-2 rounded-xl text-[12px] font-bold transition-all ${editingId === res.id ? 'bg-emerald-500 text-white shadow-md' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                         >
                           {editingId === res.id ? (
                             <>
                               <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                               Save
                             </>
                           ) : (
                             <>
                               <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                               Edit
                             </>
                           )}
                         </button>
                         <button onClick={() => deleteResource(res.id)} className="p-2 bg-slate-50 text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all rounded-xl">
                           <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                         </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : activeTab === 'collections' ? (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A]">Collections</h2>
              <button onClick={addCollection} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">Add Collection</button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {collections.map(col => (
                <div key={col.id} className="p-6 rounded-[28px] border border-slate-100 bg-white flex items-center justify-between group hover:border-blue-200 hover:shadow-md transition-all">
                  <div className="flex items-center gap-4">
                    <div className="text-2xl w-12 h-12 flex items-center justify-center bg-slate-50 rounded-2xl group-hover:bg-blue-50 transition-colors shadow-sm">{col.icon}</div>
                    <div className="font-black text-[#0F172A] tracking-tight">{col.name}</div>
                  </div>
                  <button 
                    onClick={() => removeCollection(col.id, col.name)} 
                    className="p-2 text-slate-300 hover:text-red-500 hover:bg-red-50 rounded-xl transition-all"
                    title="Remove Collection"
                  >
                    <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                  </button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">System Settings</h2>

            <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100">
              <div className="flex flex-col gap-6">
                <div>
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Typing Tagline Config</h3>
                  <p className="text-slate-500 text-sm mb-6">Manage the list of words that cycle in the sidebar typing animation.</p>

                  <div className="flex flex-wrap gap-3 mb-6">
                    {taglineWords.map((word, idx) => (
                      <div key={idx} className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-slate-200 shadow-sm group hover:border-blue-300 transition-all">
                        <span className="text-[14px] font-bold text-slate-700">{word}</span>
                        <button 
                          onClick={() => removeTaglineWord(idx)}
                          className="text-slate-300 hover:text-red-500 transition-colors"
                        >
                          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M6 18L18 6M6 6l12 12" /></svg>
                        </button>
                      </div>
                    ))}
                  </div>

                  <div className="flex gap-3 max-w-md">
                    <input 
                      type="text" 
                      placeholder="Add new word..."
                      className="flex-1 px-5 py-3 rounded-2xl bg-white border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 shadow-sm"
                      value={newTaglineWord}
                      onChange={(e) => setNewTaglineWord(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && addTaglineWord()}
                    />
                    <button 
                      onClick={addTaglineWord}
                      className="px-6 py-3 bg-[#0F172A] text-white rounded-2xl text-[13px] font-bold hover:bg-black transition-all active:scale-95 shadow-md flex items-center gap-2"
                    >
                      <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M12 4v16m8-8H4" /></svg>
                      Add
                    </button>
                  </div>
                </div>

                <div className="pt-6 border-t border-slate-200">
                  <h3 className="text-lg font-black text-[#0F172A] mb-1">Global Site Settings</h3>
                  <p className="text-slate-500 text-sm mb-4">Core repository configuration and visibility toggles.</p>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed group">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Maintenance Mode</div>
                          <div className="text-xs text-slate-400">Lock the site for all public users</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                    <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm flex items-center justify-between opacity-50 cursor-not-allowed group">
                       <div>
                          <div className="font-bold text-slate-800 text-sm">Auto-Approve Contributions</div>
                          <div className="text-xs text-slate-400">Skip the moderation mailbox queue</div>
                       </div>
                       <div className="w-10 h-5 bg-slate-200 rounded-full relative"><div className="w-3.5 h-3.5 bg-white rounded-full absolute left-1 top-0.75 shadow-sm"></div></div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingSubId, setEditingSubId] = useState<string | null>(null);
  const [editingCollectionId, setEditingCollectionId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');
  const [newSubCategoryName, setNewSubCategoryName] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // Resource Management Logic
  const updateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const deleteResource = (id: string) => {
    if (window.confirm(' PERMANENT DELETE\n\nAre you sure you want to remove this resource from the repository? This action cannot be undone.')) {
      setResources(prev => prev.filter(res => res.id !== id));
      if (editingId === id) setEditingId(null);
    }
  };

  const addResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Title',
      description: 'Describe this resource...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };
    setResources([newRes, ...resources]);
    setEditingId(newRes.id);
  };

  // Collection Management Logic
  const updateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => col.id === id ? { ...col, [field]: value } : col));
  };

  const addSubCategory = (id: string) => {
    if (!newSubCategoryName.trim()) return;
    setCollections(prev => prev.map(col => {
      if (col.id === id) {
        const subs = col.subCategories || [];
        if (subs.includes(newSubCategoryName.trim())) {
          alert("This sub-category already exists.");
          return col;
        }
        return { ...col, subCategories: [...subs, newSubCategoryName.trim()] };
      }
      return col;
    }));
    setNewSubCategoryName('');
  };

  const removeSubCategory = (id: string, sub: string) => {
    if (window.confirm(`Remove sub-category "${sub}"?`)) {
      setCollections(prev => prev.map(col => {
        if (col.id === id) {
          return { ...col, subCategories: (col.subCategories || []).filter(s => s !== sub) };
        }
        return col;
      }));
    }
  };

  const removeCollection = (id: string, name: string) => {
    if (window.confirm(` REMOVE COLLECTION: "${name.toUpperCase()}"\n\nDeleting a collection will not delete the associated resources, but they will become uncategorized. Continue?`)) {
      setCollections(prev => prev.filter(c => c.id !== id));
      if (editingCollectionId === id) setEditingCollectionId(null);
    }
  };

  const addCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    const newCol: Collection = { id: newId, name: 'New Collection', icon: '', description: '', subCategories: ['General'] };
    setCollections([...collections, newCol]);
    setEditingCollectionId(newId);
  };

  // Mailbox Logic
  const updateSubmission = (id: string, field: keyof Submission, value: any) => {
    setSubmissions(prev => prev.map(s => s.id === id ? { ...s, [field]: value } : s));
  };

  const approveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };
      setResources([newRes, ...resources]);
      setSubmissions(submissions.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
      setEditingSubId(null);
    } catch (e) {
      alert("Invalid URL. Please check the URL format before approving.");
    }
  };

  const rejectSubmission = (id: string) => {
    if (window.confirm(' REJECT SUBMISSION\n\nDiscard this user suggestion? This will remove it from the mailbox permanently.')) {
      setSubmissions(submissions.filter(s => s.id !== id));
      if (editingSubId === id) setEditingSubId(null);
    }
  };

  // Tagline Logic
  const addTaglineWord = () => {
    if (newTaglineWord.trim()) {
      setTaglineWords([...taglineWords, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const removeTaglineWord = (index: number) => {
    if (taglineWords.length > 1) {
      if (window.confirm(`Remove "${taglineWords[index]}" from the typing cycle?`)) {
        setTaglineWords(taglineWords.filter((_, i) => i !== index));
      }
    } else {
      alert("At least one word is required for the typing effect.");
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        <button onClick={() => setActiveTab('resources')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'resources' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Resources</button>
        <button onClick={() => setActiveTab('collections')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'collections' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Collections</button>
        <button onClick={() => setActiveTab('mailbox')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 relative whitespace-nowrap ${activeTab === 'mailbox' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>
          Mailbox
          {submissions.length > 0 && <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>}
        </button>
        <button onClick={() => setActiveTab('settings')} className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap ${activeTab === 'settings' ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}>Settings</button>
      </div>

      <div className="p-10">
        {activeTab === 'mailbox' ? (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">User Submissions</h2>
            {submissions.length === 0 ? (
              <div className="py-20 text-center text-slate-400">
                <svg className="w-16 h-16 mx-auto mb-4 opacity-20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2-2v10a2 2 0 002 2z" /></svg>
                <p className="font-bold uppercase tracking-widest text-[11px]">No pending submissions</p>
              </div>
            ) : (
              <div className="space-y-6">
                {submissions.map(sub => {
                  const isEditingSub = editingSubId === sub.id;
                  return (
                    <div key={sub.id} className={`p-6 rounded-3xl border transition-all flex flex-col gap-6 shadow-sm ${isEditingSub ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                      <div className="flex-1 space-y-4">
                        <div className="flex items-center gap-3">
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest bg-blue-50 px-2 py-1 rounded">SUBMISSION</span>
                          {isEditingSub ? (
                            <input 
                              className="flex-1 text-[18px] font-black text-[#0F172A] bg-white border-b-2 border-blue-400 outline-none px-2 py-1"
                              value={sub.title}
                              onChange={e => updateSubmission(sub.id, 'title', e.target.value)}
                              placeholder="Edit Title"
                            />
                          ) : (
                            <h4 className="text-[18px] font-black text-[#0F172A]">{sub.title}</h4>
                          )}
                        </div>

                        {isEditingSub ? (
                          <textarea 
                            className="w-full text-slate-500 text-sm leading-relaxed bg-white border border-slate-200 rounded-xl p-3 outline-none focus:ring-2 focus:ring-blue-500/20"
                            value={sub.description}
                            onChange={e => updateSubmission(sub.id, 'description', e.target.value)}
                            rows={3}
                            placeholder="Edit description..."
                          />
                        ) : (
                          <p className="text-slate-500 text-sm leading-relaxed">{sub.description}</p>
                        )}

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-1">
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Submitter & Credit</span>
                            {isEditingSub ? (
                              <div className="flex items-center gap-2">
                                <input 
                                  className="flex-1 text-[12px] font-bold text-slate-800 bg-white border-b border-slate-200 py-1 outline-none"
                                  value={sub.userName}
                                  onChange={e => updateSubmission(sub.id, 'userName', e.target.value)}
                                />
                                <button 
                                  onClick={() => updateSubmission(sub.id, 'wantsCredit', !sub.wantsCredit)}
                                  className={`text-[9px] font-black px-2 py-1 rounded uppercase tracking-tighter border transition-all ${sub.wantsCredit ? 'bg-blue-600 text-white border-blue-600' : 'bg-white text-slate-400 border-slate-200'}`}
                                >
                                  {sub.wantsCredit ? 'Credited' : 'Anonymous'}
                                </button>
                              </div>
                            ) : (
                              <span className="text-[12px] font-bold text-slate-800">{sub.userName} {!sub.wantsCredit && <span className="text-slate-400 font-medium">(Anonymous Pref)</span>}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Target Collection</span>
                            {isEditingSub ? (
                              <select 
                                className="text-[12px] font-bold text-[#2563EB] bg-white border-b border-slate-200 py-1 outline-none uppercase cursor-pointer"
                                value={sub.category}
                                onChange={e => updateSubmission(sub.id, 'category', e.target.value)}
                              >
                                <option value="all">General</option>
                                {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                              </select>
                            ) : (
                              <span className="text-[12px] font-bold text-[#2563EB] uppercase">{collections.find(c => c.id === sub.category)?.name || 'General'}</span>
                            )}
                          </div>
                          <div className="flex flex-col gap-1 md:col-span-2">
                            <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">URL Source</span>
                            {isEditingSub ? (
                              <input 
                                className="text-[12px] font-bold text-blue-500 bg-white border-b border-slate-200 py-1 outline-none w-full"
                                value={sub.url}
                                onChange={e => updateSubmission(sub.id, 'url', e.target.value)}
                              />
                            ) : (
                              <span className="text-[12px] font-bold text-blue-500 truncate block">{sub.url}</span>
                            )}
                          </div>
                        </div>
                      </div>
                      <div className="flex gap-2 justify-end items-center pt-2 border-t border-slate-50">
                        <button 
                          onClick={() => setEditingSubId(isEditingSub ? null : sub.id)} 
                          className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditingSub ? 'bg-emerald-50 text-emerald-600 border border-emerald-100 shadow-sm' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}
                        >
                          {isEditingSub ? (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
                              Save Draft
                            </>
                          ) : (
                            <>
                              <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" /></svg>
                              Modify
                            </>
                          )}
                        </button>
                        <button 
                          onClick={() => approveSubmission(sub)} 
                          className="flex items-center gap-2 px-8 py-2.5 bg-emerald-500 text-white rounded-xl text-[12px] font-black uppercase tracking-widest hover:bg-emerald-600 shadow-lg active:scale-95 transition-all"
                        >
                          <svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M5 13l4 4L19 7" /></svg>
