            </div>
            <div className="grid grid-cols-1 gap-6">
              {collections.map(col => {
                const isEditing = editingCollectionId === col.id;
                return (
                  <div key={col.id} className={`p-8 rounded-[32px] border transition-all ${isEditing ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50' : 'border-slate-100 bg-white hover:border-blue-200 shadow-sm'}`}>
                    <div className="flex items-center justify-between mb-6">
