              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingCollectionId, setEditingCollectionId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');
  const [newSubCategoryName, setNewSubCategoryName] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // --- RESOURCE MANAGEMENT ---
  const handleUpdateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const handleDeleteResource = (e: React.MouseEvent, id: string, title: string) => {
    e.preventDefault();
    e.stopPropagation();

    const confirmed = window.confirm(` PERMANENT DELETE\n\nAre you sure you want to remove "${title}" from the repository? This action cannot be undone.`);

    if (confirmed) {
      setResources(prev => prev.filter(res => res.id !== id));
      if (editingId === id) setEditingId(null);
    }
  };

  const handleAddResource = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Entry',
      description: 'Describe this resource here...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };

    setResources(prev => [newRes, ...prev]);
    setEditingId(newRes.id);
    setActiveTab('resources');
  };

  // --- COLLECTION MANAGEMENT ---
  const handleUpdateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => col.id === id ? { ...col, [field]: value } : col));
  };

  const handleAddSubCategory = (e: React.MouseEvent, colId: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (!newSubCategoryName.trim()) return;

    setCollections(prev => prev.map(col => {
      if (col.id === colId) {
        const subs = col.subCategories || [];
        if (subs.includes(newSubCategoryName.trim())) {
          alert("Sub-category already exists.");
          return col;
        }
        return { ...col, subCategories: [...subs, newSubCategoryName.trim()] };
      }
      return col;
    }));
    setNewSubCategoryName('');
  };

  const handleRemoveSubCategory = (e: React.MouseEvent, colId: string, subName: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (window.confirm(`Remove sub-category "${subName}"?`)) {
      setCollections(prev => prev.map(col => {
        if (col.id === colId) {
          return { ...col, subCategories: (col.subCategories || []).filter(s => s !== subName) };
        }
        return col;
      }));
    }
  };

  const handleDeleteCollection = (e: React.MouseEvent, id: string, name: string) => {
    e.preventDefault();
    e.stopPropagation();

    const confirmed = window.confirm(` REMOVE COLLECTION: "${name.toUpperCase()}"\n\nDeleting this collection will make its resources uncategorized. Continue?`);

    if (confirmed) {
      setCollections(prev => prev.filter(c => c.id !== id));
      if (editingCollectionId === id) setEditingCollectionId(null);
    }
  };

  const handleAddCollection = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    const newCol: Collection = { id: newId, name: 'New Category', icon: '', description: '', subCategories: ['General'] };

    setCollections(prev => [...prev, newCol]);
    setEditingCollectionId(newId);
    setActiveTab('collections');
  };

  // --- MAILBOX MANAGEMENT ---
  const handleApproveSubmission = (e: React.MouseEvent, sub: Submission) => {
    e.preventDefault();
    e.stopPropagation();

    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };

      setResources(prev => [newRes, ...prev]);
      setSubmissions(prev => prev.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
    } catch (err) {
      alert("Submission has an invalid URL format.");
    }
  };

  const handleRejectSubmission = (e: React.MouseEvent, id: string) => {
    e.preventDefault();
    e.stopPropagation();

    if (window.confirm(' REJECT SUBMISSION\n\nDiscard this suggestion permanently?')) {
      setSubmissions(prev => prev.filter(s => s.id !== id));
    }
  };

  // --- TAGLINE MANAGEMENT ---
  const handleAddTagline = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (newTaglineWord.trim()) {
      setTaglineWords(prev => [...prev, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const handleRemoveTagline = (e: React.MouseEvent, index: number) => {
    e.preventDefault();
    e.stopPropagation();

    if (taglineWords.length > 1) {
      if (window.confirm(`Remove "${taglineWords[index]}"?`)) {
        setTaglineWords(prev => prev.filter((_, i) => i !== index));
      }
    } else {
      alert("At least one word is required.");
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        {['resources', 'collections', 'mailbox', 'settings'].map((tab) => (
          <button 
            key={tab}
            onClick={() => setActiveTab(tab as any)} 
            className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap relative ${activeTab === tab ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}
          >
            {tab}
            {tab === 'mailbox' && submissions.length > 0 && (
              <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>
            )}
          </button>
        ))}
      </div>

      <div className="p-10">
        {activeTab === 'resources' && (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Active Vault</h2>
              <button onClick={handleAddResource} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">New Entry</button>
            </div>
            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm overflow-x-auto">
              <table className="w-full text-left border-collapse min-w-[700px]">
                <thead className="bg-[#F8FAFC] border-b border-slate-200">
                  <tr className="text-[11px] font-black uppercase tracking-widest text-slate-400">
                    <th className="px-8 py-4">Resource Info</th>
                    <th className="px-8 py-4">Collection</th>
                    <th className="px-8 py-4 text-right">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => {
                    const isEditing = editingId === res.id;
                    return (
                      <tr key={res.id} className={`group transition-colors ${isEditing ? 'bg-blue-50/40' : 'hover:bg-slate-50/50'}`}>
                        <td className="px-8 py-5">
                          {isEditing ? (
                            <div className="space-y-2 max-w-sm">
                              <input className="w-full font-bold text-[#0F172A] outline-none border-b-2 border-blue-400 bg-transparent py-1" value={res.title} onChange={e => handleUpdateResource(res.id, 'title', e.target.value)} />
                              <input className="w-full text-[10px] text-blue-500 font-bold outline-none bg-transparent" value={res.url} onChange={e => handleUpdateResource(res.id, 'url', e.target.value)} />
                            </div>
                          ) : (
                            <div>
                              <div className="font-bold text-[#0F172A] group-hover:text-blue-600 transition-colors">{res.title}</div>
                              <div className="text-[11px] text-slate-400 font-medium">{res.domain}</div>
                            </div>
                          )}
                        </td>
                        <td className="px-8 py-5">
                          {isEditing ? (
                            <select 
                              className="text-[10px] font-black px-2 py-1 rounded bg-white border border-blue-200 text-blue-600 uppercase tracking-widest outline-none"
                              value={res.category}
                              onChange={e => handleUpdateResource(res.id, 'category', e.target.value)}
                            >
                              <option value="all">General</option>
                              {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                            </select>
                          ) : (
                            <span className="text-[10px] font-black px-2 py-1 rounded bg-slate-100 text-slate-500 uppercase tracking-widest">
                              {collections.find(c => c.id === res.category)?.name || 'General'}
                            </span>
                          )}
                        </td>
                        <td className="px-8 py-5 text-right">
                          <div className="flex justify-end items-center gap-2">
                            <button 
                              onClick={(e) => { e.preventDefault(); e.stopPropagation(); setEditingId(isEditing ? null : res.id); }} 
                              className={`flex items-center gap-1.5 px-4 py-2 rounded-xl text-[12px] font-bold transition-all ${isEditing ? 'bg-emerald-500 text-white shadow-md' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                            >
                              {isEditing ? 'Save' : 'Edit'}
                            </button>
                            <button 
                              onClick={(e) => handleDeleteResource(e, res.id, res.title)} 
                              className="p-2 bg-slate-50 text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all rounded-xl"
                            >
                              <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                            </button>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'collections' && (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Categories</h2>
              <button onClick={handleAddCollection} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 transition-all">Add Category</button>
            </div>
            <div className="grid grid-cols-1 gap-6">
              {collections.map(col => {
                const isEditing = editingCollectionId === col.id;
                return (
                  <div key={col.id} className={`p-8 rounded-[32px] border transition-all ${isEditing ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50 shadow-sm' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                    <div className="flex items-center justify-between mb-6">
                      <div className="flex items-center gap-5">
                        <div className="text-2xl w-14 h-14 flex items-center justify-center bg-slate-50 rounded-[20px] shadow-sm">
                          {isEditing ? <input className="w-8 text-center bg-transparent border-b border-blue-400 outline-none" value={col.icon} onChange={e => handleUpdateCollection(col.id, 'icon', e.target.value)} maxLength={2} /> : col.icon}
                        </div>
                        <div>
                          {isEditing ? <input className="text-[20px] font-black text-[#0F172A] bg-transparent border-b-2 border-blue-400 outline-none" value={col.name} onChange={e => handleUpdateCollection(col.id, 'name', e.target.value)} /> : <div className="text-[20px] font-black text-[#0F172A]">{col.name}</div>}
                          <div className="text-[11px] font-bold text-slate-400 uppercase tracking-widest mt-1">ID: {col.id}</div>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <button onClick={(e) => { e.preventDefault(); e.stopPropagation(); setEditingCollectionId(isEditing ? null : col.id); }} className={`px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditing ? 'bg-emerald-500 text-white' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}>{isEditing ? 'Save' : 'Edit'}</button>
                        <button onClick={(e) => handleDeleteCollection(e, col.id, col.name)} className="p-2.5 text-slate-300 hover:text-red-500 hover:bg-red-50 rounded-xl transition-all"><svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg></button>
                      </div>
                    </div>
                    <div className="pt-6 border-t border-slate-100">
                      <h4 className="text-[11px] font-black text-slate-400 uppercase tracking-widest mb-4">Sub-Collections</h4>
                      <div className="flex flex-wrap gap-2 mb-4">
                        {(col.subCategories || []).map((sub, idx) => (
                          <div key={idx} className="flex items-center gap-2 bg-slate-50 border border-slate-200 px-3 py-1.5 rounded-xl group/sub transition-all hover:bg-white hover:border-blue-200">
                            <span className="text-[12px] font-bold text-slate-600">{sub}</span>
                            {isEditing && (
                              <button onClick={(e) => handleRemoveSubCategory(e, col.id, sub)} className="text-slate-300 hover:text-red-500 transition-colors"><svg className="w-3.5 h-3.5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M6 18L18 6M6 6l12 12" /></svg></button>
                            )}
                          </div>
                        ))}
                      </div>
                      {isEditing && (
                        <div className="flex gap-2 max-w-sm">
                          <input className="flex-1 px-4 py-2 bg-white border border-slate-200 rounded-xl text-[12px] font-medium outline-none focus:ring-2 focus:ring-blue-100" placeholder="New sub-collection..." value={newSubCategoryName} onChange={e => setNewSubCategoryName(e.target.value)} onKeyDown={e => e.key === 'Enter' && handleAddSubCategory(e as any, col.id)} />
                          <button onClick={(e) => handleAddSubCategory(e, col.id)} className="px-4 py-2 bg-[#0F172A] text-white rounded-xl text-[11px] font-black uppercase tracking-wider hover:bg-black transition-all">Add</button>
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {activeTab === 'mailbox' && (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Review Submissions</h2>
            {submissions.length === 0 ? (
              <div className="py-20 text-center text-slate-400">
                <svg className="w-16 h-16 mx-auto mb-4 opacity-20" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" /></svg>
                <p className="font-bold uppercase tracking-widest text-[11px]">No pending reviews</p>
              </div>
            ) : (
              <div className="space-y-6">
                {submissions.map(sub => (
                  <div key={sub.id} className="p-6 rounded-3xl border border-slate-100 bg-white hover:border-blue-200 transition-all shadow-sm">
                    <div className="flex-1 space-y-4 mb-6">
                      <div className="flex items-center gap-3">
                        <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest bg-blue-50 px-2 py-1 rounded">SUBMISSION</span>
                        <h4 className="text-[18px] font-black text-[#0F172A]">{sub.title}</h4>
                      </div>
                      <p className="text-slate-500 text-sm leading-relaxed">{sub.description}</p>
                      <div className="flex flex-wrap gap-4 text-[12px] font-bold">
                        <div className="text-slate-400 uppercase tracking-widest">By: <span className="text-slate-900">{sub.userName}</span></div>
                        <div className="text-slate-400 uppercase tracking-widest">URL: <span className="text-blue-500">{sub.url}</span></div>
                      </div>
                    </div>
                    <div className="flex gap-2 justify-end border-t border-slate-50 pt-4">
                      <button onClick={(e) => handleApproveSubmission(e, sub)} className="px-6 py-2.5 bg-emerald-500 text-white rounded-xl text-[12px] font-black uppercase tracking-widest hover:bg-emerald-600 transition-all">Approve</button>
                      <button onClick={(e) => handleRejectSubmission(e, sub.id)} className="px-6 py-2.5 text-slate-400 hover:text-red-500 hover:bg-red-50 rounded-xl text-[12px] font-bold transition-all">Reject</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'settings' && (
          <div className="space-y-8">
            <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">System Settings</h2>
            <div className="bg-slate-50 rounded-[32px] p-8 border border-slate-100">
              <h3 className="text-lg font-black text-[#0F172A] mb-1">Typing Tagline keywords</h3>
              <p className="text-slate-500 text-sm mb-6">Terms that cycle in the sidebar animation.</p>
              <div className="flex flex-wrap gap-3 mb-6">
                {taglineWords.map((word, idx) => (
                  <div key={idx} className="flex items-center gap-2 bg-white px-4 py-2 rounded-2xl border border-slate-200 shadow-sm group hover:border-blue-300 transition-all">
                    <span className="text-[14px] font-bold text-slate-700">{word}</span>
                    <button onClick={(e) => handleRemoveTagline(e, idx)} className="text-slate-300 hover:text-red-500 transition-colors"><svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path d="M6 18L18 6M6 6l12 12" /></svg></button>
                  </div>
                ))}
              </div>
              <div className="flex gap-3 max-w-md">
                <input className="flex-1 px-5 py-3 rounded-2xl bg-white border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20" placeholder="New keyword..." value={newTaglineWord} onChange={(e) => setNewTaglineWord(e.target.value)} onKeyDown={(e) => e.key === 'Enter' && handleAddTagline(e as any)} />
                <button onClick={handleAddTagline} className="px-6 py-3 bg-[#0F172A] text-white rounded-2xl text-[13px] font-bold hover:bg-black transition-all">Add</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingCollectionId, setEditingCollectionId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');
  const [newSubCategoryName, setNewSubCategoryName] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // --- RESOURCE MANAGEMENT ---
  const handleUpdateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => res.id === id ? { ...res, [field]: value } : res));
  };

  const handleDeleteResource = (id: string) => {
    // Directly delete without confirmation as requested
    setResources(prev => prev.filter(res => res.id !== id));
    if (editingId === id) setEditingId(null);
  };

  const handleAddResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource Entry',
      description: 'Describe this resource here...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'DOMAIN.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.ALL
    };

    setResources(prev => [newRes, ...prev]);
    setEditingId(newRes.id);
    setActiveTab('resources');
  };

  // --- COLLECTION MANAGEMENT ---
  const handleUpdateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => col.id === id ? { ...col, [field]: value } : col));
  };

  const handleAddSubCategory = (colId: string) => {
    if (!newSubCategoryName.trim()) return;

    setCollections(prev => prev.map(col => {
      if (col.id === colId) {
        const subs = col.subCategories || [];
        if (subs.includes(newSubCategoryName.trim())) {
          return col;
        }
        return { ...col, subCategories: [...subs, newSubCategoryName.trim()] };
      }
      return col;
    }));
    setNewSubCategoryName('');
  };

  const handleRemoveSubCategory = (colId: string, subName: string) => {
    setCollections(prev => prev.map(col => {
      if (col.id === colId) {
        return { ...col, subCategories: (col.subCategories || []).filter(s => s !== subName) };
      }
      return col;
    }));
  };

  const handleDeleteCollection = (id: string) => {
    setCollections(prev => prev.filter(c => c.id !== id));
    if (editingCollectionId === id) setEditingCollectionId(null);
  };

  const handleAddCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    const newCol: Collection = { id: newId, name: 'New Category', icon: '', description: '', subCategories: ['General'] };

    setCollections(prev => [...prev, newCol]);
    setEditingCollectionId(newId);
    setActiveTab('collections');
  };

  // --- MAILBOX MANAGEMENT ---
  const handleApproveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.ALL
      };

      setResources(prev => [newRes, ...prev]);
      setSubmissions(prev => prev.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
    } catch (err) {
      alert("Invalid URL format.");
    }
  };

  const handleRejectSubmission = (id: string) => {
    setSubmissions(prev => prev.filter(s => s.id !== id));
  };

  // --- TAGLINE MANAGEMENT ---
  const handleAddTagline = () => {
    if (newTaglineWord.trim()) {
      setTaglineWords(prev => [...prev, newTaglineWord.trim()]);
      setNewTaglineWord('');
    }
  };

  const handleRemoveTagline = (index: number) => {
    if (taglineWords.length > 1) {
      setTaglineWords(prev => prev.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="bg-white rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-2 overflow-x-auto scrollbar-hide">
        {['resources', 'collections', 'mailbox', 'settings'].map((tab) => (
          <button 
            key={tab}
            onClick={() => setActiveTab(tab as any)} 
            className={`flex-1 py-3 px-6 rounded-2xl text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap relative ${activeTab === tab ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}
          >
            {tab === 'resources' ? 'Archive' : tab.charAt(0).toUpperCase() + tab.slice(1)}
            {tab === 'mailbox' && submissions.length > 0 && (
              <span className="ml-2 bg-red-500 text-white text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>
            )}
          </button>
        ))}
      </div>

      <div className="p-10">
        {activeTab === 'resources' && (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Active Vault</h2>
              <button onClick={handleAddResource} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">New Entry</button>
            </div>
            <div className="rounded-[24px] border border-slate-200 overflow-hidden shadow-sm overflow-x-auto">
              <table className="w-full text-left border-collapse min-w-[700px]">
                <thead className="bg-[#F8FAFC] border-b border-slate-200">
                  <tr className="text-[11px] font-black uppercase tracking-widest text-slate-400">
                    <th className="px-8 py-4">Resource Info</th>
                    <th className="px-8 py-4">Collection</th>
                    <th className="px-8 py-4 text-right">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-100 bg-white">
                  {resources.map(res => {
                    const isEditing = editingId === res.id;
                    return (
                      <tr key={res.id} className={`group transition-colors ${isEditing ? 'bg-blue-50/40' : 'hover:bg-slate-50/50'}`}>
                        <td className="px-8 py-5">
                          {isEditing ? (
                            <div className="space-y-2 max-w-sm">
                              <input className="w-full font-bold text-[#0F172A] outline-none border-b-2 border-blue-400 bg-transparent py-1" value={res.title} onChange={e => handleUpdateResource(res.id, 'title', e.target.value)} />
                              <input className="w-full text-[10px] text-blue-500 font-bold outline-none bg-transparent" value={res.url} onChange={e => handleUpdateResource(res.id, 'url', e.target.value)} />
                            </div>
                          ) : (
                            <div>
                              <div className="font-bold text-[#0F172A] group-hover:text-blue-600 transition-colors">{res.title}</div>
                              <div className="text-[11px] text-slate-400 font-medium">{res.domain}</div>
                            </div>
                          )}
                        </td>
                        <td className="px-8 py-5">
                          {isEditing ? (
                            <select 
                              className="text-[10px] font-black px-2 py-1 rounded bg-white border border-blue-200 text-blue-600 uppercase tracking-widest outline-none"
                              value={res.category}
                              onChange={e => handleUpdateResource(res.id, 'category', e.target.value)}
                            >
                              <option value="all">General</option>
                              {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                            </select>
                          ) : (
                            <span className="text-[10px] font-black px-2 py-1 rounded bg-slate-100 text-slate-500 uppercase tracking-widest">
                              {collections.find(c => c.id === res.category)?.name || 'General'}
                            </span>
                          )}
                        </td>
                        <td className="px-8 py-5 text-right">
                          <div className="flex justify-end items-center gap-2">
                            <button 
                              onClick={() => setEditingId(isEditing ? null : res.id)} 
                              className={`flex items-center gap-1.5 px-4 py-2 rounded-xl text-[12px] font-bold transition-all ${isEditing ? 'bg-emerald-500 text-white shadow-md' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                            >
                              {isEditing ? 'Save' : 'Edit'}
                            </button>
                            <button 
                              onClick={() => handleDeleteResource(res.id)} 
                              className="p-2 bg-slate-50 text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all rounded-xl cursor-pointer"
                              title="Delete immediately"
                            >
                              <svg className="w-5 h-5 pointer-events-none" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                            </button>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'collections' && (
          <div className="space-y-8">
            <div className="flex justify-between items-center">
              <h2 className="text-[28px] font-[900] text-[#0F172A] tracking-tight">Categories</h2>
              <button onClick={handleAddCollection} className="bg-[#2563EB] text-white px-8 py-3 rounded-full text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 transition-all">Add Category</button>
            </div>
            <div className="grid grid-cols-1 gap-6">
              {collections.map(col => {
                const isEditing = editingCollectionId === col.id;
                return (
                  <div key={col.id} className={`p-8 rounded-[32px] border transition-all ${isEditing ? 'border-blue-400 bg-blue-50/10 ring-4 ring-blue-50 shadow-sm' : 'border-slate-100 bg-white hover:border-blue-200'}`}>
                    <div className="flex items-center justify-between mb-6">
                      <div className="flex items-center gap-5">
                        <div className="text-2xl w-14 h-14 flex items-center justify-center bg-slate-50 rounded-[20px] shadow-sm">
                          {isEditing ? <input className="w-8 text-center bg-transparent border-b border-blue-400 outline-none" value={col.icon} onChange={e => handleUpdateCollection(col.id, 'icon', e.target.value)} maxLength={2} /> : col.icon}
                        </div>
                        <div>
                          {isEditing ? <input className="text-[20px] font-black text-[#0F172A] bg-transparent border-b-2 border-blue-400 outline-none" value={col.name} onChange={e => handleUpdateCollection(col.id, 'name', e.target.value)} /> : <div className="text-[20px] font-black text-[#0F172A]">{col.name}</div>}
                          <div className="text-[11px] font-bold text-slate-400 uppercase tracking-widest mt-1">ID: {col.id}</div>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <button onClick={() => setEditingCollectionId(isEditing ? null : col.id)} className={`px-5 py-2.5 rounded-xl text-[12px] font-bold transition-all ${isEditing ? 'bg-emerald-500 text-white' : 'bg-slate-50 text-slate-500 hover:bg-slate-100'}`}>{isEditing ? 'Save' : 'Edit'}</button>
