export default ResourceCard;

import React, { useState, useMemo, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import AdminDashboard from './components/AdminDashboard';
import { COLLECTIONS as initialCollections, RESOURCES as initialResources } from './data';
import { Resource, Collection, Theme, Submission } from './types';

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>('all');
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Admin State
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(false);

  // Data State - Initialize from localStorage if available
  const [collections, setCollections] = useState<Collection[]>(() => {
    const saved = localStorage.getItem('heo_collections');
    return saved ? JSON.parse(saved) : initialCollections;
  });

  const [resources, setResources] = useState<Resource[]>(() => {
    const saved = localStorage.getItem('heo_resources');
    return saved ? JSON.parse(saved) : initialResources;
  });

  const [submissions, setSubmissions] = useState<Submission[]>(() => {
    const saved = localStorage.getItem('heo_submissions');
    return saved ? JSON.parse(saved) : [];
  });

  const [taglineWords, setTaglineWords] = useState<string[]>(() => {
    const saved = localStorage.getItem('heo_tagline');
    return saved ? JSON.parse(saved) : ['HEOR', 'MARKOV', 'PSA'];
  });

  // Save to localStorage on changes
  useEffect(() => {
    localStorage.setItem('heo_collections', JSON.stringify(collections));
  }, [collections]);

  useEffect(() => {
    localStorage.setItem('heo_resources', JSON.stringify(resources));
  }, [resources]);

  useEffect(() => {
    localStorage.setItem('heo_submissions', JSON.stringify(submissions));
  }, [submissions]);

  useEffect(() => {
    localStorage.setItem('heo_tagline', JSON.stringify(taglineWords));
  }, [taglineWords]);

  // Contribution Modal State
  const [showContributeModal, setShowContributeModal] = useState(false);
  const [contributionData, setContributionData] = useState({ 
    title: '', 
    url: '', 
    description: '', 
    userName: '', 
    category: 'all', 
    wantsCredit: true 
  });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    if (password === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    if (activeTheme === 'admin-deck') {
      setActiveTheme('all');
    }
  };

  const handlePublicContribute = () => {
    const newSubmission: Submission = {
      id: Math.random().toString(36).substr(2, 9),
      ...contributionData,
      submittedDate: new Date().toLocaleDateString('en-GB')
    };
    setSubmissions(prev => [newSubmission, ...prev]);
    setContributionSuccess(true);
    setTimeout(() => {
      setShowContributeModal(false);
      setContributionSuccess(false);
      setContributionData({ 
        title: '', 
        url: '', 
        description: '', 
        userName: '', 
        category: 'all', 
        wantsCredit: true 
      });
    }, 2000);
  };

  const filteredResources = useMemo(() => {
    return resources.filter(res => {
      const matchesTheme = activeTheme === 'all' || res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery, resources]);

  const currentCollection = useMemo(() => 
    collections.find(c => c.id === activeTheme), 
    [activeTheme, collections]
  );

  const isAdminDeckActive = activeTheme === 'admin-deck' && isAdmin;
  const collectionName = isAdminDeckActive ? 'Management' : (activeTheme === 'all' ? 'General' : currentCollection?.name || 'General');
  const subCategories = currentCollection?.subCategories || [];

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter'] antialiased">
      <Sidebar 
        collections={collections} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        onAdminClick={() => isAdmin ? handleLogout() : setShowLoginModal(true)}
        isAdminActive={isAdmin}
        notificationCount={submissions.length}
        taglineWords={taglineWords}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white px-4 md:px-8 pt-4 md:pt-8 pb-5 select-none border-b border-[#F1F5F9]/60 relative z-30">
          <div className="max-w-[1440px] mx-auto">
            {/* Mobile Nav Top Bar */}
            <div className="flex items-center justify-between mb-4 lg:hidden">
              <div className="flex items-center gap-2">
                <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center text-white font-black text-xs">H</div>
                <span className="font-black text-lg tracking-tighter">HEORepo</span>
              </div>
              <button 
                onClick={() => setIsSidebarOpen(true)}
                className="p-2 hover:bg-slate-50 rounded-xl transition-colors"
              >
                <svg className="w-6 h-6 text-slate-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                  <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>

            <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 md:gap-8 mb-6">
              <div className="flex flex-col gap-2 md:gap-3">
                <div className="flex items-center gap-3 md:gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#2563EB] uppercase antialiased transition-all duration-500">
                    {isAdminDeckActive ? 'ADMIN CONTROL PANEL' : (activeTheme === 'all' ? 'GENERAL' : (currentCollection?.name.toUpperCase() || 'UNKNOWN'))}
                  </div>
                  {!isAdminDeckActive && (
                    <div className="text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#64748B] uppercase antialiased">
                      {filteredResources.length} ITEMS
                    </div>
                  )}
                </div>
                <h1 className="text-[32px] md:text-[48px] font-[900] tracking-[-0.04em] leading-[0.9] text-[#0F172A] antialiased transition-all duration-500">
                  {collectionName} <span className="text-[#D1D5DB] font-medium">{isAdminDeckActive ? 'Deck' : 'Vault'}</span>
                </h1>
              </div>

              <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-3 md:gap-6 pb-1">
                {!isAdminDeckActive && (
                  <div className="relative w-full sm:w-[280px] md:w-[380px]">
                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-[#94A3B8]">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 md:h-5 md:w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                      </svg>
                    </div>
                    <input
                      type="text"
                      placeholder="Search..."
                      className="block w-full pl-10 md:pl-12 pr-6 py-2.5 md:py-3.5 bg-[#F8FAFC] border border-[#F1F5F9] rounded-full text-[14px] md:text-[15px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#4F46E5] focus:bg-white outline-none shadow-sm transition-all duration-300 placeholder-[#94A3B8]"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>
                )}

                <button 
                  onClick={() => isAdmin ? handleLogout() : setShowContributeModal(true)}
                  className={`flex items-center justify-center gap-2 px-6 md:px-8 py-2.5 md:py-3.5 ${isAdminDeckActive ? 'bg-[#0F172A]' : 'bg-[#2563EB]'} text-white text-[14px] md:text-[15px] font-[700] rounded-full hover:opacity-90 transition-all duration-300 shadow-md active:scale-[0.96]`}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-4 h-4 md:w-5 md:h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="3">
                    <path strokeLinecap="round" strokeLinejoin="round" d={isAdminDeckActive ? "M15 19l-7-7 7-7" : "M12 4v16m8-8H4"} />
                  </svg>
                  <span className="whitespace-nowrap">{isAdminDeckActive ? 'Exit Admin' : 'Contribute'}</span>
                </button>
              </div>
            </div>

            {/* Sub-category Filter Bar */}
            {!isAdminDeckActive && activeTheme !== 'all' && subCategories.length > 0 && (
              <div className="flex items-center gap-2 md:gap-3 overflow-x-auto pb-2 -mx-4 px-4 scrollbar-hide animate-card-entry">
                <button
                  onClick={() => setActiveSubCategory('all')}
                  className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                    activeSubCategory === 'all'
                    ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                    : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                  }`}
                >
                  All {collectionName}s
                </button>
                {subCategories.map((sub, idx) => (
                  <button
                    key={sub}
                    onClick={() => setActiveSubCategory(sub)}
                    className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                      activeSubCategory === sub
                      ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                      : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                    }`}
                    style={{ transitionDelay: `${idx * 0.02}s` }}
                  >
                    {sub}
                  </button>
                ))}
              </div>
            )}
          </div>
        </header>

        {/* Scrollable Content Area */}
        <div className="flex-1 overflow-y-auto px-4 md:px-8 py-6 md:py-10 scroll-smooth">
          <div className="max-w-[1440px] mx-auto">
            {isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center py-20 md:py-32 text-center animate-card-entry">
                  <div className="w-14 h-14 md:w-16 md:h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-4 md:mb-6 shadow-sm">
                    <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                      <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                  <h4 className="text-lg md:text-xl font-[800] text-[#111827] mb-2">Empty Archive</h4>
                  <p className="text-[13px] md:text-[14px] text-[#6B7280]">Adjust your filters or try a different search term.</p>
                </div>
              )
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-4 md:px-8 flex flex-col md:flex-row items-center justify-between gap-2">
          <div className="flex items-center gap-4 md:gap-8">
            <span className="text-[9px] md:text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase whitespace-nowrap">VERSION 2.4.0</span>
            {isAdmin && <span className="text-[9px] md:text-[10px] font-bold text-[#2563EB] tracking-[0.15em] uppercase whitespace-nowrap">PRIVILEGES GRANTED</span>}
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'bg-emerald-500'} animate-pulse`}></div>
             <span className="text-[9px] md:text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? 'ADMIN MODE' : 'SYNC SECURE'}</span>
          </div>
        </footer>
      </main>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-2">Admin Portal</h2>
            <p className="text-slate-500 text-xs md:text-sm mb-6">Enter system password to access management tools.</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3.5 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono text-[16px]`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">Incorrect Password</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                <button onClick={handleLogin} className="flex-[2] py-3.5 rounded-2xl bg-[#0F172A] text-white text-[13px] font-bold hover:bg-black shadow-lg">Confirm</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Public Contribution Modal */}
      {showContributeModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
