    title: '', 
    url: '', 
    description: '', 
    userName: '', 
    category: 'all', 
    wantsCredit: true 
  });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    if (password === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
