const DARK_NAVY = '#121A26';
const SLATE_LIGHTER = '#94A3B8'; 
const SLATE_EXTRA_LIGHTER = '#CBD5E1'; 

const TypingTagline: React.FC<{ words: string[] }> = ({ words }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentText, setCurrentText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [typingSpeed, setTypingSpeed] = useState(75); 

  useEffect(() => {
    if (!words || words.length === 0) return;

    const handleTyping = () => {
      const fullWord = words[currentWordIndex % words.length];
      if (!isDeleting) {
        setCurrentText(fullWord.substring(0, currentText.length + 1));
        setTypingSpeed(75);
        if (currentText === fullWord) {
          setTimeout(() => setIsDeleting(true), 1200);
        }
      } else {
        setCurrentText(fullWord.substring(0, currentText.length - 1));
        setTypingSpeed(50);
        if (currentText === '') {
          setIsDeleting(false);
          setCurrentWordIndex((prev) => (prev + 1) % words.length);
        }
      }
    };
    const timer = setTimeout(handleTyping, typingSpeed);
    return () => clearTimeout(timer);
  }, [currentText, isDeleting, currentWordIndex, typingSpeed, words]);

  if (!words || words.length === 0) return null;

  return (
    <span style={{ color: BRAND_BLUE, fontWeight: 900, lineHeight: '1' }}>
      {currentText || '\u00A0'}
    </span>
  );
};

