                        value={contributionData.userName}
                        onChange={(e) => setContributionData({...contributionData, userName: e.target.value})}
                      />
                    </div>
                    <label className="flex items-center gap-3 group cursor-pointer select-none py-1">
                      <div className={`w-5 h-5 rounded border-2 transition-all flex items-center justify-center ${contributionData.wantsCredit ? 'bg-[#2563EB] border-[#2563EB]' : 'border-slate-300'}`}>
                        {contributionData.wantsCredit && <svg className="w-3.5 h-3.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={4}><path d="M5 13l4 4L19 7" /></svg>}
                      </div>
                      <input 
                        type="checkbox" 
                        className="hidden" 
                        checked={contributionData.wantsCredit} 
                        onChange={() => setContributionData({...contributionData, wantsCredit: !contributionData.wantsCredit})}
                      />
                      <span className="text-sm font-bold text-slate-600">Credit my name</span>
                    </label>
                  </div>
                  <div className="flex gap-3 pt-2">
                    <button onClick={() => setShowContributeModal(false)} className="flex-1 py-3.5 rounded-xl md:rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                    <button onClick={handlePublicContribute} className="flex-[2] py-3.5 rounded-xl md:rounded-2xl bg-[#2563EB] text-white text-[13px] font-bold hover:bg-blue-600 shadow-lg active:scale-95 transition-all">Submit</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useMemo, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import AdminDashboard from './components/AdminDashboard';
import { COLLECTIONS as initialCollections, RESOURCES as initialResources } from './data';
import { Resource, Collection, Theme, Submission } from './types';

const BRAND_BLUE = '#2563EB';
const DARK_NAVY = '#121A26';

/**
 * Security Utility: Sanitizes input strings to prevent XSS and 
 * injection attacks when handling user-provided content.
 */
const sanitizeInput = (str: string): string => {
  if (!str) return '';
  const temp = document.createElement('div');
  temp.textContent = str;
  return temp.innerHTML;
};

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>(Theme.GENERAL);
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Admin State
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(false);

  // Data State - Initialize from localStorage if available
  const [collections, setCollections] = useState<Collection[]>(() => {
    const saved = localStorage.getItem('heo_collections');
    return saved ? JSON.parse(saved) : initialCollections;
  });

  const [resources, setResources] = useState<Resource[]>(() => {
    const saved = localStorage.getItem('heo_resources');
    return saved ? JSON.parse(saved) : initialResources;
  });

  const [submissions, setSubmissions] = useState<Submission[]>(() => {
    const saved = localStorage.getItem('heo_submissions');
    return saved ? JSON.parse(saved) : [];
  });

  const [taglineWords, setTaglineWords] = useState<string[]>(() => {
    const saved = localStorage.getItem('heo_tagline');
    return saved ? JSON.parse(saved) : ['HEOR', 'MARKOV', 'PSA'];
  });

  // Save to localStorage on changes
  useEffect(() => {
    localStorage.setItem('heo_collections', JSON.stringify(collections));
  }, [collections]);

  useEffect(() => {
    localStorage.setItem('heo_resources', JSON.stringify(resources));
  }, [resources]);

  useEffect(() => {
    localStorage.setItem('heo_submissions', JSON.stringify(submissions));
  }, [submissions]);

  useEffect(() => {
    localStorage.setItem('heo_tagline', JSON.stringify(taglineWords));
  }, [taglineWords]);

  // Contribution Modal State
  const [showContributeModal, setShowContributeModal] = useState(false);
  const [contributionData, setContributionData] = useState({ 
    title: '', 
    url: '', 
    description: '', 
    userName: '', 
    category: Theme.GENERAL, 
    wantsCredit: true 
  });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    // Simple authentication check with basic input protection
    if (password.trim() === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    if (activeTheme === 'admin-deck') {
      setActiveTheme(Theme.GENERAL);
    }
  };

  const handlePublicContribute = () => {
    // Basic protection: Sanitize inputs before adding to submissions
    const newSubmission: Submission = {
      id: Math.random().toString(36).substr(2, 9),
      title: sanitizeInput(contributionData.title),
      url: sanitizeInput(contributionData.url),
      description: sanitizeInput(contributionData.description),
      userName: sanitizeInput(contributionData.userName),
      category: contributionData.category,
      wantsCredit: contributionData.wantsCredit,
      submittedDate: new Date().toLocaleDateString('en-GB')
    };
    setSubmissions(prev => [newSubmission, ...prev]);
    setContributionSuccess(true);
    setTimeout(() => {
      setShowContributeModal(false);
      setContributionSuccess(false);
      setContributionData({ 
        title: '', 
        url: '', 
        description: '', 
        userName: '', 
        category: Theme.GENERAL, 
        wantsCredit: true 
      });
    }, 2000);
  };

  const filteredResources = useMemo(() => {
    return resources.filter(res => {
      const matchesTheme = res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery, resources]);

  const currentCollection = useMemo(() => 
    collections.find(c => c.id === activeTheme), 
    [activeTheme, collections]
  );

  const isAdminDeckActive = activeTheme === 'admin-deck' && isAdmin;
  const collectionName = isAdminDeckActive ? 'Management' : currentCollection?.name || 'Archive';
  const subCategories = currentCollection?.subCategories || [];

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter'] antialiased">
      <Sidebar 
        collections={collections} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        onAdminClick={() => isAdmin ? handleLogout() : setShowLoginModal(true)}
        isAdminActive={isAdmin}
        notificationCount={submissions.length}
        taglineWords={taglineWords}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white px-4 md:px-8 pt-4 md:pt-8 pb-5 select-none border-b border-[#F1F5F9]/60 relative z-30">
          <div className="max-w-[1440px] mx-auto">
            {/* Mobile Nav Top Bar */}
            <div className="flex items-center justify-between mb-4 lg:hidden">
              <div className="flex items-center gap-2 select-none">
                <div className="flex items-center font-[900] text-[26px] tracking-[-0.04em] leading-none antialiased">
                  <span style={{ color: DARK_NAVY }}>HEO</span>
                  <span style={{ 
                    backgroundImage: `linear-gradient(to right, ${DARK_NAVY} 50%, ${BRAND_BLUE} 50%)`,
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    display: 'inline-block',
                    position: 'relative',
                  }}>R</span>
                  <span style={{ color: BRAND_BLUE }}>epo</span>
                  <div 
                    className="ml-[1.5px] self-end" 
                    style={{ 
                      backgroundColor: BRAND_BLUE, 
                      width: '6.5px', 
                      height: '6.5px',
                      marginBottom: '3.5px' 
                    }}
                  ></div>
                </div>
              </div>
              <button 
                onClick={() => setIsSidebarOpen(true)}
                className="p-2 hover:bg-slate-50 rounded-xl transition-colors"
              >
                <svg className="w-6 h-6 text-slate-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                  <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>

            <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 md:gap-8 mb-6">
              <div className="flex flex-col gap-2 md:gap-3">
                <div className="flex items-center gap-3 md:gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#2563EB] uppercase antialiased transition-all duration-500">
                    {isAdminDeckActive ? 'ADMIN CONTROL PANEL' : (currentCollection?.name.toUpperCase() || 'UNKNOWN')}
                  </div>

                  {!isAdminDeckActive && (
                    <div className="text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#64748B] uppercase antialiased">
                      {filteredResources.length} ITEMS
                    </div>
                  )}
                </div>
                <h1 className="text-[32px] md:text-[48px] font-[900] tracking-[-0.04em] leading-[0.9] text-[#0F172A] antialiased transition-all duration-500">
                  {collectionName} <span className="text-[#D1D5DB] font-[900]">{isAdminDeckActive ? 'Deck' : 'Vault'}</span>
                </h1>
              </div>

              <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-3 md:gap-6 pb-1">
                {!isAdminDeckActive && (
                  <div className="relative w-full sm:w-[280px] md:w-[380px]">
                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-[#94A3B8]">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 md:h-5 md:w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                      </svg>
                    </div>
                    <input
                      type="text"
                      placeholder="Search..."
                      className="block w-full pl-10 md:pl-12 pr-6 py-2.5 md:py-3.5 bg-[#F8FAFC] border border-[#F1F5F9] rounded-full text-[14px] md:text-[15px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#4F46E5] focus:bg-white outline-none shadow-sm transition-all duration-300 placeholder-[#94A3B8]"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>
                )}

                <button 
                  onClick={() => isAdmin ? handleLogout() : setShowContributeModal(true)}
                  className={`flex items-center justify-center gap-2 px-6 md:px-8 py-2.5 md:py-3.5 ${isAdminDeckActive ? 'bg-[#0F172A]' : 'bg-[#2563EB]'} text-white text-[14px] md:text-[15px] font-[700] rounded-full hover:opacity-90 transition-all duration-300 shadow-md active:scale-[0.96]`}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-4 h-4 md:w-5 md:h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="3">
                    <path strokeLinecap="round" strokeLinejoin="round" d={isAdminDeckActive ? "M15 19l-7-7 7-7" : "M12 4v16m8-8H4"} />
                  </svg>
                  <span className="whitespace-nowrap">{isAdminDeckActive ? 'Exit Admin' : 'Contribute'}</span>
                </button>
              </div>
            </div>

            {/* Sub-category Filter Bar */}
            {!isAdminDeckActive && subCategories.length > 0 && (
              <div className="flex items-center gap-2 md:gap-3 overflow-x-auto pb-2 -mx-4 px-4 scrollbar-hide animate-card-entry">
                <button
                  onClick={() => setActiveSubCategory('all')}
                  className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                    activeSubCategory === 'all'
                    ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                    : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                  }`}
                >
                  All {collectionName}s
                </button>
                {subCategories.map((sub, idx) => (
                  <button
                    key={sub}
                    onClick={() => setActiveSubCategory(sub)}
                    className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                      activeSubCategory === sub
                      ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                      : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                    }`}
                    style={{ transitionDelay: `${idx * 0.02}s` }}
                  >
                    {sub}
                  </button>
                ))}
              </div>
            )}
          </div>
        </header>

        {/* Scrollable Content Area */}
        <div className="flex-1 overflow-y-auto px-4 md:px-8 py-6 md:py-10 scroll-smooth">
          <div className="max-w-[1440px] mx-auto">
            {isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center py-20 md:py-32 text-center animate-card-entry">
                  <div className="w-14 h-14 md:w-16 md:h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-4 md:mb-6 shadow-sm">
                    <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                      <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                  <h4 className="text-lg md:text-xl font-[800] text-[#111827] mb-2">Empty Archive</h4>
                  <p className="text-[13px] md:text-[14px] text-[#6B7280]">Adjust your filters or try a different search term.</p>
                </div>
              )
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-4 md:px-8 flex flex-col md:flex-row items-center justify-between gap-2">
          <div className="flex items-center gap-4 md:gap-8">
            <span className="text-[9px] md:text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase whitespace-nowrap">VERSION 1.0.0</span>
            {isAdmin && <span className="text-[9px] md:text-[10px] font-bold text-[#2563EB] tracking-[0.15em] uppercase whitespace-nowrap">PRIVILEGES GRANTED</span>}
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'hidden'} animate-pulse`}></div>
             <span className="text-[9px] md:text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? 'ADMIN MODE' : ''}</span>
          </div>
        </footer>
      </main>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-2">Admin Portal</h2>
            <p className="text-slate-500 text-xs md:text-sm mb-6">Enter system password to access management tools.</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3.5 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono text-[16px]`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">Incorrect Password</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                <button onClick={handleLogin} className="flex-[2] py-3.5 rounded-2xl bg-[#0F172A] text-white text-[13px] font-bold hover:bg-black shadow-lg">Confirm</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Public Contribution Modal */}
      {showContributeModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[500px] border border-slate-100 overflow-y-auto max-h-[90vh]">
            {contributionSuccess ? (
              <div className="text-center py-8">
                <div className="w-14 h-14 md:w-16 md:h-16 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                </div>
                <h3 className="text-lg md:text-xl font-black text-[#0F172A] mb-2">Submission Received</h3>
                <p className="text-slate-500 text-xs md:text-sm">Thank you! Our curators will review your contribution shortly.</p>
              </div>
            ) : (
              <>
                <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-1 md:mb-2">Contribute</h2>
                <p className="text-slate-500 text-xs md:text-sm mb-6">Suggest a high-quality HEOR resource.</p>
                <div className="space-y-4">
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Title</label>
                    <input 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                      placeholder="Resource Title"
                      value={contributionData.title}
                      onChange={(e) => setContributionData({...contributionData, title: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">URL</label>
                    <input 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                      placeholder="https://..."
                      value={contributionData.url}
                      onChange={(e) => setContributionData({...contributionData, url: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Suggested Collection</label>
                    <div className="relative">
                      <select 
                        className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 appearance-none cursor-pointer text-[16px]"
                        value={contributionData.category}
                        onChange={(e) => setContributionData({...contributionData, category: e.target.value})}
                      >
                        {collections.map(col => (
                          <option key={col.id} value={col.id}>{col.name}</option>
                        ))}
                      </select>
                      <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                        <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M19 9l-7 7-7-7" /></svg>
                      </div>
                    </div>
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Description</label>
                    <textarea 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 min-h-[80px] md:min-h-[100px] text-[16px]"
                      placeholder="Brief description..."
                      value={contributionData.description}
                      onChange={(e) => setContributionData({...contributionData, description: e.target.value})}
                    />
                  </div>
                  <div className="flex flex-col gap-4">
                    <div>
                      <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Your Name</label>
                      <input 
                        className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                        placeholder="Your Name"
                        value={contributionData.userName}
                        onChange={(e) => setContributionData({...contributionData, userName: e.target.value})}
                      />
                    </div>
                    <label className="flex items-center gap-3 group cursor-pointer select-none py-1">
                      <div className={`w-5 h-5 rounded border-2 transition-all flex items-center justify-center ${contributionData.wantsCredit ? 'bg-[#2563EB] border-[#2563EB]' : 'border-slate-300'}`}>
                        {contributionData.wantsCredit && <svg className="w-3.5 h-3.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={4}><path d="M5 13l4 4L19 7" /></svg>}
                      </div>
                      <input 
                        type="checkbox" 
                        className="hidden" 
                        checked={contributionData.wantsCredit} 
                        onChange={() => setContributionData({...contributionData, wantsCredit: !contributionData.wantsCredit})}
                      />
                      <span className="text-sm font-bold text-slate-600">Credit my name</span>
                    </label>
                  </div>
                  <div className="flex gap-3 pt-2">
                    <button onClick={() => setShowContributeModal(false)} className="flex-1 py-3.5 rounded-xl md:rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                    <button onClick={handlePublicContribute} className="flex-[2] py-3.5 rounded-xl md:rounded-2xl bg-[#2563EB] text-white text-[13px] font-bold hover:bg-blue-600 shadow-lg active:scale-95 transition-all">Submit</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useMemo, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
import AdminDashboard from './components/AdminDashboard';
import { COLLECTIONS as initialCollections, RESOURCES as initialResources } from './data';
import { Resource, Collection, Theme, Submission } from './types';

const BRAND_BLUE = '#2563EB';
const DARK_NAVY = '#121A26';

/**
 * Security Utility: Sanitizes input strings to prevent XSS and 
 * injection attacks when handling user-provided content.
 */
const sanitizeInput = (str: string): string => {
  if (!str) return '';
  const temp = document.createElement('div');
  temp.textContent = str;
  return temp.innerHTML;
};

const App: React.FC = () => {
  const [activeTheme, setActiveTheme] = useState<string>(Theme.GENERAL);
  const [activeSubCategory, setActiveSubCategory] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Admin State
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState(false);

  // Data State - Initialize from localStorage if available
  const [collections, setCollections] = useState<Collection[]>(() => {
    const saved = localStorage.getItem('heo_collections');
    return saved ? JSON.parse(saved) : initialCollections;
  });

  const [resources, setResources] = useState<Resource[]>(() => {
    const saved = localStorage.getItem('heo_resources');
    return saved ? JSON.parse(saved) : initialResources;
  });

  const [submissions, setSubmissions] = useState<Submission[]>(() => {
    const saved = localStorage.getItem('heo_submissions');
    return saved ? JSON.parse(saved) : [];
  });

  const [taglineWords, setTaglineWords] = useState<string[]>(() => {
    const saved = localStorage.getItem('heo_tagline');
    return saved ? JSON.parse(saved) : ['HEOR', 'MARKOV', 'PSA'];
  });

  // Save to localStorage on changes
  useEffect(() => {
    localStorage.setItem('heo_collections', JSON.stringify(collections));
  }, [collections]);

  useEffect(() => {
    localStorage.setItem('heo_resources', JSON.stringify(resources));
  }, [resources]);

  useEffect(() => {
    localStorage.setItem('heo_submissions', JSON.stringify(submissions));
  }, [submissions]);

  useEffect(() => {
    localStorage.setItem('heo_tagline', JSON.stringify(taglineWords));
  }, [taglineWords]);

  // Contribution Modal State
  const [showContributeModal, setShowContributeModal] = useState(false);
  const [contributionData, setContributionData] = useState({ 
    title: '', 
    url: '', 
    description: '', 
    userName: '', 
    category: Theme.GENERAL, 
    wantsCredit: true 
  });
  const [contributionSuccess, setContributionSuccess] = useState(false);

  useEffect(() => {
    setActiveSubCategory('all');
  }, [activeTheme]);

  const handleLogin = () => {
    // Basic credential check with password-only flow
    if (password.trim() === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    if (activeTheme === 'admin-deck') {
      setActiveTheme(Theme.GENERAL);
    }
  };

  const handlePublicContribute = () => {
    // Sanitize user inputs for security protection
    const newSubmission: Submission = {
      id: Math.random().toString(36).substr(2, 9),
      title: sanitizeInput(contributionData.title),
      url: sanitizeInput(contributionData.url),
      description: sanitizeInput(contributionData.description),
      userName: sanitizeInput(contributionData.userName),
      category: contributionData.category,
      wantsCredit: contributionData.wantsCredit,
      submittedDate: new Date().toLocaleDateString('en-GB')
    };
    setSubmissions(prev => [newSubmission, ...prev]);
    setContributionSuccess(true);
    setTimeout(() => {
      setShowContributeModal(false);
      setContributionSuccess(false);
      setContributionData({ 
        title: '', 
        url: '', 
        description: '', 
        userName: '', 
        category: Theme.GENERAL, 
        wantsCredit: true 
      });
    }, 2000);
  };

  const filteredResources = useMemo(() => {
    return resources.filter(res => {
      const matchesTheme = res.category === activeTheme;
      const matchesSubCategory = activeSubCategory === 'all' || res.subCategory?.toLowerCase() === activeSubCategory.toLowerCase();
      const matchesSearch = res.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            res.description.toLowerCase().includes(searchQuery.toLowerCase());
      return matchesTheme && matchesSubCategory && matchesSearch;
    });
  }, [activeTheme, activeSubCategory, searchQuery, resources]);

  const currentCollection = useMemo(() => 
    collections.find(c => c.id === activeTheme), 
    [activeTheme, collections]
  );

  const isAdminDeckActive = activeTheme === 'admin-deck' && isAdmin;
  const collectionName = isAdminDeckActive ? 'Management' : currentCollection?.name || 'Archive';
  const subCategories = currentCollection?.subCategories || [];

  return (
    <div className="flex h-screen bg-[#FCFDFF] text-slate-900 overflow-hidden font-['Inter'] antialiased">
      <Sidebar 
        collections={collections} 
        activeTheme={activeTheme} 
        onThemeSelect={setActiveTheme} 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        onAdminClick={() => isAdmin ? handleLogout() : setShowLoginModal(true)}
        isAdminActive={isAdmin}
        notificationCount={submissions.length}
        taglineWords={taglineWords}
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white px-4 md:px-8 pt-4 md:pt-8 pb-5 select-none border-b border-[#F1F5F9]/60 relative z-30">
          <div className="max-w-[1440px] mx-auto">
            {/* Mobile Nav Top Bar */}
            <div className="flex items-center justify-between mb-4 lg:hidden">
              <div className="flex items-center gap-2 select-none">
                <div className="flex items-center font-[900] text-[26px] tracking-[-0.04em] leading-none antialiased">
                  <span style={{ color: DARK_NAVY }}>HEO</span>
                  <span style={{ 
                    backgroundImage: `linear-gradient(to right, ${DARK_NAVY} 50%, ${BRAND_BLUE} 50%)`,
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    display: 'inline-block',
                    position: 'relative',
                  }}>R</span>
                  <span style={{ color: BRAND_BLUE }}>epo</span>
                  <div 
                    className="ml-[1.5px] self-end" 
                    style={{ 
                      backgroundColor: BRAND_BLUE, 
                      width: '6.5px', 
                      height: '6.5px',
                      marginBottom: '3.5px' 
                    }}
                  ></div>
                </div>
              </div>
              <button 
                onClick={() => setIsSidebarOpen(true)}
                className="p-2 hover:bg-slate-50 rounded-xl transition-colors"
              >
                <svg className="w-6 h-6 text-slate-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                  <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>

            <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 md:gap-8 mb-6">
              <div className="flex flex-col gap-2 md:gap-3">
                <div className="flex items-center gap-3 md:gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#2563EB] uppercase antialiased transition-all duration-500">
                    {isAdminDeckActive ? 'ADMIN CONTROL PANEL' : (currentCollection?.name.toUpperCase() || 'UNKNOWN')}
                  </div>

                  {!isAdminDeckActive && (
                    <div className="text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#64748B] uppercase antialiased">
                      {filteredResources.length} ITEMS
                    </div>
                  )}
                </div>
                <h1 className="text-[32px] md:text-[48px] font-[900] tracking-[-0.04em] leading-[0.9] text-[#0F172A] antialiased transition-all duration-500">
                  {collectionName} <span className="text-[#D1D5DB] font-[900]">{isAdminDeckActive ? 'Deck' : 'Vault'}</span>
                </h1>
              </div>

              <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-3 md:gap-6 pb-1">
                {!isAdminDeckActive && (
                  <div className="relative w-full sm:w-[280px] md:w-[380px]">
                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-[#94A3B8]">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 md:h-5 md:w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                      </svg>
                    </div>
                    <input
                      type="text"
                      placeholder="Search..."
                      className="block w-full pl-10 md:pl-12 pr-6 py-2.5 md:py-3.5 bg-[#F8FAFC] border border-[#F1F5F9] rounded-full text-[14px] md:text-[15px] font-medium text-[#1E293B] focus:ring-1 focus:ring-[#4F46E5] focus:bg-white outline-none shadow-sm transition-all duration-300 placeholder-[#94A3B8]"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>
                )}

                <button 
                  onClick={() => isAdmin ? handleLogout() : setShowContributeModal(true)}
                  className={`flex items-center justify-center gap-2 px-6 md:px-8 py-2.5 md:py-3.5 ${isAdminDeckActive ? 'bg-[#0F172A]' : 'bg-[#2563EB]'} text-white text-[14px] md:text-[15px] font-[700] rounded-full hover:opacity-90 transition-all duration-300 shadow-md active:scale-[0.96]`}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="w-4 h-4 md:w-5 md:h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="3">
                    <path strokeLinecap="round" strokeLinejoin="round" d={isAdminDeckActive ? "M15 19l-7-7 7-7" : "M12 4v16m8-8H4"} />
                  </svg>
                  <span className="whitespace-nowrap">{isAdminDeckActive ? 'Exit Admin' : 'Contribute'}</span>
                </button>
              </div>
            </div>

            {/* Sub-category Filter Bar */}
            {!isAdminDeckActive && subCategories.length > 0 && (
              <div className="flex items-center gap-2 md:gap-3 overflow-x-auto pb-2 -mx-4 px-4 scrollbar-hide animate-card-entry">
                <button
                  onClick={() => setActiveSubCategory('all')}
                  className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                    activeSubCategory === 'all'
                    ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                    : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                  }`}
                >
                  All {collectionName}s
                </button>
                {subCategories.map((sub, idx) => (
                  <button
                    key={sub}
                    onClick={() => setActiveSubCategory(sub)}
                    className={`px-5 md:px-6 py-2 md:py-2.5 rounded-full text-[10px] md:text-[11px] font-[800] tracking-[0.1em] uppercase transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) whitespace-nowrap active:scale-95 ${
                      activeSubCategory === sub
                      ? 'bg-[#0F172A] text-white shadow-lg shadow-slate-200'
                      : 'bg-white text-[#475569] border border-[#E2E8F0] hover:border-[#CBD5E1]'
                    }`}
                    style={{ transitionDelay: `${idx * 0.02}s` }}
                  >
                    {sub}
                  </button>
                ))}
              </div>
            )}
          </div>
        </header>

        {/* Scrollable Content Area */}
        <div className="flex-1 overflow-y-auto px-4 md:px-8 py-6 md:py-10 scroll-smooth">
          <div className="max-w-[1440px] mx-auto">
            {isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center py-20 md:py-32 text-center animate-card-entry">
                  <div className="w-14 h-14 md:w-16 md:h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-4 md:mb-6 shadow-sm">
                    <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                      <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                  <h4 className="text-lg md:text-xl font-[800] text-[#111827] mb-2">Empty Archive</h4>
                  <p className="text-[13px] md:text-[14px] text-[#6B7280]">Adjust your filters or try a different search term.</p>
                </div>
              )
            )}
          </div>
        </div>

        {/* Status Bottom Bar */}
        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-4 md:px-8 flex flex-col md:flex-row items-center justify-between gap-2">
          <div className="flex items-center gap-4 md:gap-8">
            <span className="text-[9px] md:text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase whitespace-nowrap">VERSION 1.0.0</span>
            {isAdmin && <span className="text-[9px] md:text-[10px] font-bold text-[#2563EB] tracking-[0.15em] uppercase whitespace-nowrap">PRIVILEGES GRANTED</span>}
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'hidden'} animate-pulse`}></div>
             <span className="text-[9px] md:text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? 'ADMIN MODE' : ''}</span>
          </div>
        </footer>
      </main>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-2">Admin Portal</h2>
            <p className="text-slate-500 text-xs md:text-sm mb-6">Enter system password to access management tools.</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3.5 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono text-[16px]`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">Incorrect Password</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                <button onClick={handleLogin} className="flex-[2] py-3.5 rounded-2xl bg-[#0F172A] text-white text-[13px] font-bold hover:bg-black shadow-lg">Confirm</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Public Contribution Modal */}
      {showContributeModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[500px] border border-slate-100 overflow-y-auto max-h-[90vh]">
            {contributionSuccess ? (
              <div className="text-center py-8">
                <div className="w-14 h-14 md:w-16 md:h-16 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                </div>
                <h3 className="text-lg md:text-xl font-black text-[#0F172A] mb-2">Submission Received</h3>
                <p className="text-slate-500 text-xs md:text-sm">Thank you! Our curators will review your contribution shortly.</p>
              </div>
            ) : (
              <>
                <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-1 md:mb-2">Contribute</h2>
                <p className="text-slate-500 text-xs md:text-sm mb-6">Suggest a high-quality HEOR resource.</p>
                <div className="space-y-4">
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Title</label>
                    <input 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                      placeholder="Resource Title"
                      value={contributionData.title}
                      onChange={(e) => setContributionData({...contributionData, title: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">URL</label>
                    <input 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                      placeholder="https://..."
                      value={contributionData.url}
                      onChange={(e) => setContributionData({...contributionData, url: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Suggested Collection</label>
                    <div className="relative">
                      <select 
                        className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 appearance-none cursor-pointer text-[16px]"
                        value={contributionData.category}
                        onChange={(e) => setContributionData({...contributionData, category: e.target.value})}
                      >
                        {collections.map(col => (
                          <option key={col.id} value={col.id}>{col.name}</option>
                        ))}
                      </select>
                      <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                        <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M19 9l-7 7-7-7" /></svg>
                      </div>
                    </div>
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Description</label>
                    <textarea 
                      className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 min-h-[80px] md:min-h-[100px] text-[16px]"
                      placeholder="Brief description..."
                      value={contributionData.description}
                      onChange={(e) => setContributionData({...contributionData, description: e.target.value})}
                    />
                  </div>
                  <div className="flex flex-col gap-4">
                    <div>
                      <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">Your Name</label>
                      <input 
                        className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]"
                        placeholder="Your Name"
                        value={contributionData.userName}
                        onChange={(e) => setContributionData({...contributionData, userName: e.target.value})}
                      />
                    </div>
                    <label className="flex items-center gap-3 group cursor-pointer select-none py-1">
                      <div className={`w-5 h-5 rounded border-2 transition-all flex items-center justify-center ${contributionData.wantsCredit ? 'bg-[#2563EB] border-[#2563EB]' : 'border-slate-300'}`}>
                        {contributionData.wantsCredit && <svg className="w-3.5 h-3.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={4}><path d="M5 13l4 4L19 7" /></svg>}
                      </div>
                      <input 
                        type="checkbox" 
                        className="hidden" 
                        checked={contributionData.wantsCredit} 
                        onChange={() => setContributionData({...contributionData, wantsCredit: !contributionData.wantsCredit})}
                      />
                      <span className="text-sm font-bold text-slate-600">Credit my name</span>
                    </label>
                  </div>
                  <div className="flex gap-3 pt-2">
                    <button onClick={() => setShowContributeModal(false)} className="flex-1 py-3.5 rounded-xl md:rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">Cancel</button>
                    <button onClick={handlePublicContribute} className="flex-[2] py-3.5 rounded-xl md:rounded-2xl bg-[#2563EB] text-white text-[13px] font-bold hover:bg-blue-600 shadow-lg active:scale-95 transition-all">Submit</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState } from 'react';
import { Resource } from '../types';

interface ResourceCardProps {
  resource: Resource;
  index: number;
}

const DARK_NAVY = '#111827';
const LIGHT_SLATE = '#94A3B8';
const THEME_BLUE = '#2563EB';

const ResourceCard: React.FC<ResourceCardProps> = ({ resource, index }) => {
  const [bookmarked, setBookmarked] = useState(resource.isBookmarked || false);

  const handleCardClick = () => {
    if (resource.url) {
      window.open(resource.url, '_blank', 'noopener,noreferrer');
    }
  };

  const shouldHideContributor = 
    resource.contributor === 'Admin' || 
    resource.contributor === 'System Admin' || 
    resource.contributor === 'Anonymous';

  return (
    <div 
      onClick={handleCardClick}
      className="bg-white rounded-[24px] border border-[#E5E7EB] flex flex-col overflow-hidden group transition-all duration-500 cubic-bezier(0.16, 1, 0.3, 1) hover:shadow-[0_25px_50px_-12px_rgba(0,0,0,0.1)] hover:-translate-y-1 relative w-full max-w-[320px] mx-auto cursor-pointer animate-card-entry"
      style={{ animationDelay: `${index * 0.05}s` }}
    >
      {/* Image Section */}
      <div className="h-48 relative overflow-hidden shrink-0">
        <img src={resource.imageUrl} alt={resource.title} className="w-full h-full object-cover transition-transform duration-1000 group-hover:scale-105" />

        {/* Modern Hover Overlay */}
        <div className="absolute inset-0 bg-[#0F172A]/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-out" />

        {/* Visit Resource Button Overlay */}
        <div className="absolute bottom-6 left-1/2 -translate-x-1/2 bg-[#2563EB] px-6 py-3 rounded-full flex items-center justify-center gap-2.5 opacity-0 translate-y-4 group-hover:translate-y-0 group-hover:opacity-100 transition-all duration-300 cubic-bezier(0.23, 1, 0.32, 1) z-10 shadow-2xl border border-white/20">
          <span className="text-white font-[900] text-[11px] tracking-[0.1em] uppercase antialiased whitespace-nowrap">Visit Resource</span>
          <svg className="w-4 h-4 text-white" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 4H4V20H20V12" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M10 14L20 4M20 4H14M20 4V10" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </div>
      </div>

      <div className="p-[24px] flex flex-col flex-1">
        {/* Title */}
        <h3 className="font-[800] leading-[1.3] mb-3 group-hover:text-[#2563EB] transition-colors duration-500" style={{ color: DARK_NAVY, fontSize: '19px' }}>
          {resource.title}
        </h3>

        {/* Description */}
        <p className="leading-[1.6] font-normal mb-6 transition-colors duration-500 text-slate-500" style={{ fontSize: '14px' }}>
          {resource.description}
        </p>

        <div className="mt-auto pt-6 border-t border-slate-50 flex items-center justify-between">
          <div className="flex items-center gap-3.5">
            <div className="w-10 h-10 rounded-xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-blue-50 group-hover:text-blue-500 transition-all duration-500">
              <svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2.5"><path d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg>
            </div>
            <div className="flex flex-col">
              <span className="font-[800] uppercase tracking-[0.15em] leading-tight mb-0.5" style={{ color: LIGHT_SLATE, fontSize: '10px' }}>UPLOADED</span>
              <span className="font-[800] leading-tight text-[#0F172A]" style={{ fontSize: '14px' }}>{resource.addedDate}</span>

              <div className="h-[18px]">
                {!shouldHideContributor && (
                  <div className="font-[700] antialiased mt-1" style={{ color: THEME_BLUE, fontSize: '11px' }}>
                    by {resource.contributor}
                  </div>
                )}
              </div>
            </div>
          </div>
          <button 
            onClick={(e) => { 
              e.preventDefault(); 
              e.stopPropagation(); 
              setBookmarked(!bookmarked); 
            }} 
            className="focus:outline-none transition-all duration-300 hover:scale-125 p-2 rounded-full hover:bg-slate-50 relative z-20 active:scale-90"
          >
            <svg className={`w-6 h-6 transition-all duration-500 ${bookmarked ? 'text-[#2563EB]' : 'text-[#CBD5E1]'}`} viewBox="0 0 24 24" fill="currentColor">
              <path d="M17 3H7c-1.1 0-1.99.9-1.99 2L5 21l7-3 7 3V5c0-1.1-.9-2-2-2z" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ResourceCard;

import React, { useState, useMemo, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import ResourceCard from './components/ResourceCard';
