  const handleLogin = () => {
    // Basic protection: trim and check. Real world would use hash/token.
    if (password.trim() === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
