              >
                <svg className="w-6 h-6 text-slate-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2.5}>
                  <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>

            <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 md:gap-8 mb-6">
              <div className="flex flex-col gap-2 md:gap-3">
                <div className="flex items-center gap-3 md:gap-4">
                  <div className="px-3 py-1 rounded-full bg-[#EFF6FF] border border-[#2563EB]/20 text-[9px] md:text-[10px] font-[800] tracking-[0.12em] md:tracking-[0.15em] text-[#2563EB] uppercase antialiased transition-all duration-500">
                    {isAdminDeckActive ? 'ADMIN CONTROL PANEL' : (activeTheme === 'all' ? 'FULL REPOSITORY' : (currentCollection?.name.toUpperCase() || 'UNKNOWN'))}
                  </div>

                  {!isAdminDeckActive && (
