            </div>
          </div>

          <div className="flex items-center gap-2">
            <button 
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                setBookmarked(!bookmarked);
              }}
              className="focus:outline-none transition-all duration-300 hover:scale-110 p-2 rounded-xl hover:bg-slate-50 active:scale-90"
              title="Bookmark"
            >
              <svg className={`w-6 h-6 transition-all duration-500 ${bookmarked ? 'text-[#2563EB]' : 'text-[#CBD5E1]'}`} viewBox="0 0 24 24" fill="currentColor">
                <path d="M17 3H7c-1.1 0-1.99.9-1.99 2L5 21l7-3 7 3V5c0-1.1-.9-2-2-2z" />
              </svg>
            </button>

            <div className="h-6 w-[1px] bg-slate-100 mx-1"></div>

            <button 
              onClick={handleCardClick}
              className="flex items-center gap-1.5 pl-2 py-2 text-[#2563EB] group/btn transition-all duration-300 hover:translate-x-0.5"
            >
              <span className="text-[10px] font-[900] uppercase tracking-wider antialiased">Visit Resource</span>
              <svg className="w-3.5 h-3.5 transition-transform duration-300 group-hover/btn:-translate-y-0.5 group-hover/btn:translate-x-0.5" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M7 17L17 7M17 7H7M17 7V17" stroke="currentColor" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

