const BRAND_BLUE = '#2563EB';
const DARK_NAVY = '#121A26';

/**
 * SECURITY: Sanitizes strings by removing HTML tags to prevent XSS.
 */
const sanitizeData = (str: string): string => {
  if (typeof str !== 'string') return '';
  return str.replace(/<[^>]*>?/gm, '').trim();
};

/**
 * SECURITY: Validates URL protocol to prevent malicious injection.
 */
const isValidLink = (url: string): boolean => {
  try {
    const parsed = new URL(url.startsWith('http') ? url : `https://${url}`);
    return parsed.protocol === 'http:' || parsed.protocol === 'https:';
  } catch {
    return false;
  }
};

