const SLATE_LIGHTER = '#94A3B8'; 
const SLATE_EXTRA_LIGHTER = '#CBD5E1'; 

const sidebarTranslations = {
  en: {
    explore: "EXPLORE",
    home: "Home",
    fullArchive: "Full Archive",
    collections: "COLLECTIONS",
    adminConsole: "ADMIN CONSOLE",
    adminDeck: "Admin Deck",
