                    <span style={{ color: DARK_NAVY }}>HEO</span>
                    <span style={{ 
                      backgroundImage: `linear-gradient(to right, ${DARK_NAVY} 50%, ${BRAND_BLUE} 50%)`,
                      WebkitBackgroundClip: 'text',
                      WebkitTextFillColor: 'transparent',
                      display: 'inline-block',
                      position: 'relative',
                    }}>R</span>
                    <span style={{ color: BRAND_BLUE }}>epo</span>
                    {/* Fixed duplicate className attribute below by merging the two occurrences into one. */}
                    <div 
                      className="ml-[4px] md:ml-[8px] self-end w-[10px] h-[10px] md:w-[16px] md:h-[16px] mb-[6px] md:mb-[10px]" 
                      style={{ 
                        backgroundColor: BRAND_BLUE, 
                        width: '12px', 
                        height: '12px',
                        marginBottom: '6px' 
                      }}
                    ></div>
                  </div>
                </h1>

                <div className="flex flex-col items-center gap-8">
                  <div className="flex flex-wrap justify-center gap-4 max-w-2xl px-4">
                    {collections.map((col, idx) => (
                      <button 
                        key={col.id}
                        onClick={() => setActiveTheme(col.id)}
                        className="px-6 py-4 bg-white border border-slate-200 rounded-2xl font-bold text-slate-600 hover:border-blue-500 hover:text-blue-600 hover:shadow-xl transition-all active:scale-95 flex items-center gap-3"
                        style={{ animationDelay: `${idx * 0.1}s` }}
                      >
                        <span className="text-lg">{col.icon}</span>
                        <span>{col.name}</span>
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            ) : isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
