  const handleLogin = () => {
    if (password.trim() === '123456') {
      setIsAdmin(true);
      setShowLoginModal(false);
      setPassword('');
      setLoginError(false);
      setActiveTheme('admin-deck');
    } else {
      setLoginError(true);
