                    }}
                  ></div>
                </div>

                {/* Line 3: Instructional text */}
                <div className="flex flex-col items-center gap-10">
                  <div className="text-slate-900 font-[800] text-xl md:text-3xl tracking-tight antialiased">
                    {t.select}
                  </div>

                  {/* Buttons */}
                  <div className="flex flex-wrap justify-center gap-4 max-w-2xl px-4">
                    {collections.map((col, idx) => (
                      <button 
                        key={col.id}
                        onClick={() => setActiveTheme(col.id)}
                        className="px-6 py-4 bg-white border border-slate-200 rounded-2xl font-bold text-slate-600 hover:border-blue-500 hover:text-blue-600 hover:shadow-xl transition-all active:scale-95 flex items-center gap-3"
                        style={{ animationDelay: `${idx * 0.1}s` }}
                      >
                        <span className="text-lg">{col.icon}</span>
                        <span>{col.name}</span>
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            ) : isAdminDeckActive ? (
              <AdminDashboard 
                resources={resources} 
                setResources={setResources} 
                collections={collections}
                setCollections={setCollections}
                submissions={submissions}
                setSubmissions={setSubmissions}
                taglineWords={taglineWords}
                setTaglineWords={setTaglineWords}
                initialTab="resources"
              />
            ) : (
              filteredResources.length > 0 ? (
                <div 
                  key={`${activeTheme}-${activeSubCategory}`} 
                  className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:gap-[24px] justify-items-center"
                >
                  {filteredResources.map((res, index) => (
                    <ResourceCard key={res.id} resource={res} index={index} />
                  ))}
                </div>
              ) : (
                <div className="flex flex-col items-center justify-center py-20 md:py-32 text-center animate-card-entry">
                  <div className="w-14 h-14 md:w-16 md:h-16 bg-white border border-[#E5E7EB] rounded-2xl flex items-center justify-center text-[#9CA3AF] mb-4 md:mb-6 shadow-sm">
                    <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={1.5}>
                      <path d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                  <h4 className="text-lg md:text-xl font-[800] text-[#111827] mb-2">{t.emptyTitle}</h4>
                  <p className="text-[13px] md:text-[14px] text-[#6B7280]">{t.emptyDesc}</p>
                </div>
              )
            )}
          </div>
        </div>

        <footer className="bg-white border-t border-[#E5E7EB] py-3 px-4 md:px-8 flex flex-col md:flex-row items-center justify-between gap-2">
          <div className="flex items-center gap-4 md:gap-8">
            <span className="text-[9px] md:text-[10px] font-bold text-[#94A3B8] tracking-[0.15em] uppercase whitespace-nowrap">{t.version} 1.0.0</span>
          </div>
          <div className="flex items-center gap-2">
             <div className={`w-1.5 h-1.5 rounded-full ${isAdmin ? 'bg-amber-500' : 'hidden'} animate-pulse`}></div>
             <span className="text-[9px] md:text-[10px] font-bold text-[#64748B] tracking-[0.05em] uppercase">{isAdmin ? t.adminMode : ''}</span>
          </div>
        </footer>
      </main>

      {showLoginModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[400px] border border-slate-100">
            <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-2">{t.adminPortal}</h2>
            <p className="text-slate-500 text-xs md:text-sm mb-6">{t.adminPortalDesc}</p>
            <div className="space-y-4">
              <div>
                <input 
                  type="password"
                  placeholder={t.password}
                  value={password}
                  onChange={(e) => { setPassword(e.target.value); setLoginError(false); }}
                  onKeyDown={(e) => e.key === 'Enter' && handleLogin()}
                  className={`w-full px-5 py-3.5 rounded-2xl bg-slate-50 border ${loginError ? 'border-red-500 ring-1 ring-red-100' : 'border-slate-200'} outline-none focus:ring-2 focus:ring-[#2563EB]/20 transition-all font-mono text-[16px]`}
                />
                {loginError && <p className="text-red-500 text-[10px] font-bold uppercase tracking-wider mt-2 ml-1">{t.incorrectPassword}</p>}
              </div>
              <div className="flex gap-3">
                <button onClick={() => setShowLoginModal(false)} className="flex-1 py-3.5 rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">{t.cancel}</button>
                <button onClick={handleLogin} className="flex-[2] py-3.5 rounded-2xl bg-[#0F172A] text-white text-[13px] font-bold hover:bg-black shadow-lg">{t.confirm}</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {showContributeModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/40 backdrop-blur-sm animate-card-entry">
          <div className="bg-white p-6 md:p-8 rounded-[24px] md:rounded-[32px] shadow-2xl w-full max-w-[500px] border border-slate-100 overflow-y-auto max-h-[90vh]">
            {contributionSuccess ? (
              <div className="text-center py-8">
                <div className="w-14 h-14 md:w-16 md:h-16 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-7 h-7 md:w-8 md:h-8" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={3}><path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" /></svg>
                </div>
                <h3 className="text-lg md:text-xl font-black text-[#0F172A] mb-2">{t.subReceived}</h3>
                <p className="text-slate-500 text-xs md:text-sm">{t.subReceivedDesc}</p>
              </div>
            ) : (
              <>
                <h2 className="text-xl md:text-2xl font-[900] text-[#0F172A] mb-1 md:mb-2">{t.contributeTitle}</h2>
                <p className="text-slate-500 text-xs md:text-sm mb-6">{t.contributeDesc}</p>
                <div className="space-y-4">
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">{t.title}</label>
                    <input className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]" placeholder={t.title} value={contributionData.title} onChange={(e) => setContributionData({...contributionData, title: e.target.value})}/>
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">{t.url}</label>
                    <input className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]" placeholder="https://..." value={contributionData.url} onChange={(e) => setContributionData({...contributionData, url: e.target.value})}/>
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">{t.suggestedCol}</label>
                    <div className="relative">
                      <select className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 appearance-none cursor-pointer text-[16px]" value={contributionData.category} onChange={(e) => setContributionData({...contributionData, category: e.target.value})}>
                        {collections.map(col => <option key={col.id} value={col.id}>{col.name}</option>)}
                      </select>
                      <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                        <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M19 9l-7 7-7-7" /></svg>
                      </div>
                    </div>
                  </div>
                  <div>
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">{t.description}</label>
                    <textarea className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 min-h-[80px] md:min-h-[100px] text-[16px]" placeholder={t.description} value={contributionData.description} onChange={(e) => setContributionData({...contributionData, description: e.target.value})}/>
                  </div>
                  <div className="flex flex-col gap-4">
                    <div>
                      <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest block mb-1.5 ml-1">{t.yourName}</label>
                      <input className="w-full px-4 md:px-5 py-3 rounded-xl md:rounded-2xl bg-slate-50 border border-slate-200 outline-none focus:ring-2 focus:ring-[#2563EB]/20 text-[16px]" placeholder={t.yourName} value={contributionData.userName} onChange={(e) => setContributionData({...contributionData, userName: e.target.value})}/>
                    </div>
                    <label className="flex items-center gap-3 group cursor-pointer select-none py-1">
                      <div className={`w-5 h-5 rounded border-2 transition-all flex items-center justify-center ${contributionData.wantsCredit ? 'bg-[#2563EB] border-[#2563EB]' : 'border-slate-300'}`}>
                        {contributionData.wantsCredit && <svg className="w-3.5 h-3.5 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={4}><path d="M5 13l4 4L19 7" /></svg>}
                      </div>
                      <input type="checkbox" className="hidden" checked={contributionData.wantsCredit} onChange={() => setContributionData({...contributionData, wantsCredit: !contributionData.wantsCredit})}/>
                      <span className="text-sm font-bold text-slate-600">{t.creditName}</span>
                    </label>
                  </div>
                  <div className="flex gap-3 pt-2">
                    <button onClick={() => setShowContributeModal(false)} className="flex-1 py-3.5 rounded-xl md:rounded-2xl text-[13px] font-bold text-slate-500 hover:bg-slate-50">{t.cancel}</button>
                    <button onClick={handlePublicContribute} className="flex-[2] py-3.5 rounded-xl md:rounded-2xl bg-[#2563EB] text-white text-[13px] font-bold hover:bg-blue-600 shadow-lg active:scale-95 transition-all">{t.submit}</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

import React, { useState, useEffect } from 'react';
import { Resource, Collection, Theme, Submission } from '../types';

interface AdminDashboardProps {
  resources: Resource[];
  setResources: React.Dispatch<React.SetStateAction<Resource[]>>;
  collections: Collection[];
  setCollections: React.Dispatch<React.SetStateAction<Collection[]>>;
  submissions: Submission[];
  setSubmissions: React.Dispatch<React.SetStateAction<Submission[]>>;
  taglineWords: string[];
  setTaglineWords: React.Dispatch<React.SetStateAction<string[]>>;
  initialTab?: 'resources' | 'collections' | 'mailbox' | 'settings';
}

/**
 * SECURITY: Sanitizes strings by removing HTML tags.
 */
const sanitizeInput = (str: string): string => {
  if (typeof str !== 'string') return '';
  return str.replace(/<[^>]*>?/gm, '').trim();
};

const AdminDashboard: React.FC<AdminDashboardProps> = ({ 
  resources, 
  setResources, 
  collections, 
  setCollections, 
  submissions, 
  setSubmissions, 
  taglineWords, 
  setTaglineWords, 
  initialTab = 'resources' 
}) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingCollectionId, setEditingCollectionId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'resources' | 'collections' | 'mailbox' | 'settings'>(initialTab as any);
  const [newTaglineWord, setNewTaglineWord] = useState('');
  const [newSubCategory, setNewSubCategory] = useState('');

  useEffect(() => {
    setActiveTab(initialTab as any);
  }, [initialTab]);

  // --- RESOURCE MANAGEMENT ---
  const handleUpdateResource = (id: string, field: keyof Resource, value: any) => {
    setResources(prev => prev.map(res => {
      if (res.id === id) {
        let finalValue = value;
        // Sanitize text fields
        if (typeof value === 'string' && (field === 'title' || field === 'description' || field === 'contributor')) {
          finalValue = sanitizeInput(value);
        }

        const updated = { ...res, [field]: finalValue };

        if (field === 'url') {
          try {
            const urlObj = new URL(value.startsWith('http') ? value : `https://${value}`);
            updated.domain = urlObj.hostname.toUpperCase();
          } catch (e) {}
        }
        return updated;
      }
      return res;
    }));
  };

  const handleDeleteResource = (id: string) => {
    setResources(prev => prev.filter(res => res.id !== id));
    if (editingId === id) setEditingId(null);
  };

  const handleAddResource = () => {
    const newRes: Resource = {
      id: Math.random().toString(36).substr(2, 9),
      title: 'New Resource',
      description: 'Brief description...',
      contributor: 'Admin',
      url: 'https://',
      domain: 'NEW.COM',
      imageUrl: 'https://images.unsplash.com/photo-1551288049-bbda38a594a0?auto=format&fit=crop&q=80&w=600',
      addedDate: new Date().toLocaleDateString('en-GB'),
      category: Theme.GENERAL
    };

    setResources(prev => [newRes, ...prev]);
    setEditingId(newRes.id);
    setActiveTab('resources');
  };

  // --- COLLECTION MANAGEMENT ---
  const handleUpdateCollection = (id: string, field: keyof Collection, value: any) => {
    setCollections(prev => prev.map(col => {
      if (col.id === id) {
        let finalValue = value;
        if (typeof value === 'string' && (field === 'name' || field === 'description')) {
          finalValue = sanitizeInput(value);
        }
        return { ...col, [field]: finalValue };
      }
      return col;
    }));
  };

  const handleAddSubCategory = (colId: string) => {
    const cleanSub = sanitizeInput(newSubCategory);
    if (!cleanSub) return;

    setCollections(prev => prev.map(col => {
      if (col.id === colId) {
        const subs = col.subCategories || [];
        if (subs.includes(cleanSub)) return col;
        return { ...col, subCategories: [...subs, cleanSub] };
      }
      return col;
    }));
    setNewSubCategory('');
  };

  const handleRemoveSubCategory = (colId: string, subName: string) => {
    setCollections(prev => prev.map(col => {
      if (col.id === colId) {
        return { 
          ...col, 
          subCategories: (col.subCategories || []).filter(s => s !== subName) 
        };
      }
      return col;
    }));
  };

  const handleDeleteCollection = (id: string) => {
    setCollections(prev => prev.filter(c => c.id !== id));
    if (editingCollectionId === id) setEditingCollectionId(null);
  };

  const handleAddCollection = () => {
    const newId = `custom-${Math.random().toString(36).substr(2, 5)}`;
    const newCol: Collection = { id: newId, name: 'New Category', icon: '', description: '', subCategories: ['General'] };
    setCollections(prev => [...prev, newCol]);
    setEditingCollectionId(newId);
    setActiveTab('collections');
  };

  // --- MAILBOX MANAGEMENT ---
  const handleApproveSubmission = (sub: Submission) => {
    try {
      const urlToUse = sub.url.startsWith('http') ? sub.url : `https://${sub.url}`;
      const domain = new URL(urlToUse).hostname.toUpperCase();

      const newRes: Resource = {
        id: Math.random().toString(36).substr(2, 9),
        title: sub.title,
        description: sub.description,
        contributor: sub.wantsCredit ? sub.userName : 'Anonymous',
        url: urlToUse,
        domain: domain,
        imageUrl: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?auto=format&fit=crop&q=80&w=600',
        addedDate: new Date().toLocaleDateString('en-GB'),
        category: sub.category || Theme.GENERAL
      };

      setResources(prev => [newRes, ...prev]);
      setSubmissions(prev => prev.filter(s => s.id !== sub.id));
      setActiveTab('resources');
      setEditingId(newRes.id);
    } catch (err) {
      alert("Invalid URL format.");
    }
  };

  const handleRejectSubmission = (id: string) => {
    setSubmissions(prev => prev.filter(s => s.id !== id));
  };

  // --- TAGLINE MANAGEMENT ---
  const handleAddTagline = () => {
    const cleanWord = sanitizeInput(newTaglineWord);
    if (cleanWord) {
      setTaglineWords(prev => [...prev, cleanWord]);
      setNewTaglineWord('');
    }
  };

  const handleRemoveTagline = (index: number) => {
    if (taglineWords.length > 1) {
      setTaglineWords(prev => prev.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="bg-white rounded-[24px] md:rounded-[32px] border border-slate-200 shadow-xl overflow-hidden animate-card-entry ring-1 ring-black/5">
      <div className="flex bg-slate-50/50 border-b border-slate-200 p-2 gap-1.5 md:gap-2 overflow-x-auto scrollbar-hide">
        {['resources', 'collections', 'mailbox', 'settings'].map((tab) => (
          <button 
            key={tab}
            onClick={() => setActiveTab(tab as any)} 
            className={`flex-1 py-2.5 md:py-3 px-4 md:px-6 rounded-xl md:rounded-2xl text-[10px] md:text-[12px] font-[800] uppercase tracking-wider transition-all duration-300 whitespace-nowrap relative ${activeTab === tab ? 'bg-white text-[#2563EB] shadow-sm border border-slate-200' : 'text-slate-500 hover:text-slate-700'}`}
          >
            {tab === 'resources' ? 'Vault' : tab.charAt(0).toUpperCase() + tab.slice(1)}
            {tab === 'mailbox' && submissions.length > 0 && (
              <span className="ml-2 bg-red-500 text-white text-[8px] md:text-[9px] px-1.5 py-0.5 rounded-full ring-2 ring-white">{submissions.length}</span>
            )}
          </button>
        ))}
      </div>

      <div className="p-4 md:p-10">
        {activeTab === 'resources' && (
          <div className="space-y-6 md:space-y-8">
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
              <div>
                <h2 className="text-[24px] md:text-[28px] font-[900] text-[#0F172A] tracking-tight">Management</h2>
                <p className="text-slate-500 text-xs md:text-sm mt-1">Adjust titles, URLs, and categorizations.</p>
              </div>
              <button onClick={handleAddResource} className="w-full sm:w-auto bg-[#2563EB] text-white px-6 md:px-8 py-2.5 md:py-3 rounded-full text-[11px] md:text-[12px] font-black uppercase tracking-widest shadow-lg hover:opacity-90 active:scale-95 transition-all">Add Resource</button>
            </div>

            <div className="rounded-[16px] md:rounded-[24px] border border-slate-200 overflow-hidden shadow-sm">
              <div className="overflow-x-auto">
                <table className="w-full text-left border-collapse min-w-[700px]">
                  <thead className="bg-[#F8FAFC] border-b border-slate-200">
                    <tr className="text-[9px] md:text-[11px] font-black uppercase tracking-widest text-slate-400">
                      <th className="px-4 md:px-8 py-4">Resource</th>
                      <th className="px-4 md:px-8 py-4">Link (URL)</th>
                      <th className="px-4 md:px-8 py-4">Collection</th>
                      <th className="px-4 md:px-8 py-4 text-right">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-slate-100 bg-white">
                    {resources.map(res => {
                      const isEditing = editingId === res.id;
                      return (
                        <tr key={res.id} className={`group transition-colors ${isEditing ? 'bg-blue-50/40' : 'hover:bg-slate-50/50'}`}>
                          <td className="px-4 md:px-8 py-5">
                            {isEditing ? (
                              <div className="space-y-1.5 max-w-xs">
                                <input 
                                  className="w-full font-bold text-[#0F172A] text-sm outline-none border-b-2 border-blue-400 bg-transparent py-0.5" 
                                  value={res.title} 
                                  onChange={e => handleUpdateResource(res.id, 'title', e.target.value)} 
                                />
                                <textarea 
                                  className="w-full text-[11px] text-slate-500 bg-transparent outline-none resize-none"
                                  value={res.description}
                                  onChange={e => handleUpdateResource(res.id, 'description', e.target.value)}
                                  rows={2}
                                />
                              </div>
                            ) : (
                              <div className="max-w-[180px] md:max-w-xs">
                                <div className="font-bold text-[#0F172A] text-sm group-hover:text-blue-600 transition-colors truncate">{res.title}</div>
                                <div className="text-[10px] md:text-[11px] text-slate-400 font-medium line-clamp-1">{res.description}</div>
                              </div>
                            )}
                          </td>
                          <td className="px-4 md:px-8 py-5">
                            {isEditing ? (
                              <div className="space-y-1.5">
                                <input 
                                  className="w-full text-[11px] text-blue-500 font-bold outline-none bg-white border border-blue-100 rounded px-2 py-1" 
                                  value={res.url} 
                                  onChange={e => handleUpdateResource(res.id, 'url', e.target.value)} 
                                />
                                <div className="text-[9px] text-slate-400 font-black uppercase">{res.domain}</div>
                              </div>
                            ) : (
                              <div className="max-w-[150px] md:max-w-[200px]">
                                <div className="text-[11px] text-blue-600 font-bold truncate">{res.url}</div>
                                <div className="text-[9px] text-slate-400 font-bold uppercase tracking-wider">{res.domain}</div>
                              </div>
                            )}
                          </td>
                          <td className="px-4 md:px-8 py-5">
                            {isEditing ? (
                              <select 
                                className="text-[9px] md:text-[10px] font-black px-2 py-1 rounded bg-white border border-blue-200 text-blue-600 uppercase tracking-widest outline-none"
                                value={res.category}
                                onChange={e => handleUpdateResource(res.id, 'category', e.target.value)}
                              >
                                {collections.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                              </select>
                            ) : (
                              <span className="text-[9px] md:text-[10px] font-black px-2 py-1 rounded bg-slate-100 text-slate-500 uppercase tracking-widest">
                                {collections.find(c => c.id === res.category)?.name || 'General'}
                              </span>
                            )}
                          </td>
                          <td className="px-4 md:px-8 py-5 text-right">
                            <div className="flex justify-end items-center gap-1.5 md:gap-2">
                              <button 
                                onClick={() => setEditingId(isEditing ? null : res.id)} 
                                className={`flex items-center gap-1.5 px-3 md:px-4 py-1.5 md:py-2 rounded-xl text-[10px] md:text-[12px] font-bold transition-all ${isEditing ? 'bg-emerald-500 text-white shadow-md' : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                              >
                                {isEditing ? 'Save' : 'Adjust'}
                              </button>
                              <button 
                                onClick={() => handleDeleteResource(res.id)} 
                                className="p-1.5 md:p-2 bg-slate-50 text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all rounded-xl cursor-pointer"
                              >
                                <svg className="w-4 h-4 md:w-5 md:h-5 pointer-events-none" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>
                              </button>
                            </div>
                          </td>
